import { useEffect, useMemo, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { Card, CardContent } from "../components/ui/card";
import { Badge } from "../components/ui/badge";
import { Button } from "../components/ui/button";
import { Skeleton } from "../components/ui/skeleton";
import { Separator } from "../components/ui/separator";
import { ToggleGroup, ToggleGroupItem } from "../components/ui/toggle-group";

type Tool = {
  id?: number | string;
  name: string;
  description?: string;
  tags?: string[];
  icon?: string | null;
  link?: string | null;
};

const API_BASE = process.env.REACT_APP_API_BASE || "http://127.0.0.1:8000";

export default function CategoryView() {
  const { cap = "" } = useParams();
  const [loading, setLoading] = useState(true);
  const [tools, setTools] = useState<Tool[]>([]);
  const [filter, setFilter] = useState<string | null>(null);

  useEffect(() => {
    let cancelled = false;
    setLoading(true);
    (async () => {
      try {
        const r = await fetch(`${API_BASE}/catalog/categories/${encodeURIComponent(cap)}`);
        const data = await r.json();
        const arr: Tool[] = data?.tools || data || [];
        if (!cancelled) {
          setTools(arr.map((t: any) => ({
            id: t.id ?? t.name,
            name: t.name,
            description: t.description ?? "",
            tags: t.tags ?? [],
            icon: t.icon ?? t.icon_url ?? null,
            link: t.link ?? t.links?.website ?? null,
          })));
          setLoading(false);
        }
      } catch {
        if (!cancelled) { setTools([]); setLoading(false); }
      }
    })();
    return () => { cancelled = true; };
  }, [cap]);

  const shown = useMemo(() => {
    if (!filter) return tools;
    const f = filter.toLowerCase();
    return tools.filter((t) => (t.tags || []).some((x) => (x || "").toLowerCase().includes(f)));
  }, [tools, filter]);

  return (
    <main className="mx-auto max-w-7xl px-4 py-8">
      <div className="flex items-end justify-between gap-4">
        <div>
          <h1 className="text-2xl md:text-3xl font-bold tracking-tight break-words">{cap}</h1>
          <p className="text-muted-foreground mt-1">Tools in this category. Use filters to refine your view.</p>
        </div>
        <ToggleGroup type="single" value={filter ?? ""} onValueChange={(v) => setFilter(v || null)} className="bg-muted rounded-xl p-1">
          <ToggleGroupItem value="" aria-label="All">All</ToggleGroupItem>
          <ToggleGroupItem value="free" aria-label="Free">Free</ToggleGroupItem>
          <ToggleGroupItem value="trending" aria-label="Trending">Trending</ToggleGroupItem>
          <ToggleGroupItem value="new" aria-label="New">New</ToggleGroupItem>
        </ToggleGroup>
      </div>

      <Separator className="my-6" />

      {loading ? (
        <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-4">
          {Array.from({ length: 9 }).map((_, i) => (<Skeleton key={i} className="h-40 rounded-2xl" />))}
        </div>
      ) : shown.length === 0 ? (
        <div className="text-sm text-muted-foreground">No tools found.</div>
      ) : (
        <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-4">
          {shown.map((t) => (
            <a key={t.id} href={t.link || "#"} target="_blank" rel="noreferrer" className="group">
              <Card className="rounded-2xl h-full hover:shadow-md transition">
                <CardContent className="pt-5">
                  <div className="flex items-center gap-3 mb-2">
                    {t.icon ? <img src={t.icon} alt="" className="h-6 w-6 rounded" /> : <span className="text-lg">🧰</span>}
                    <span className="truncate font-semibold">{t.name}</span>
                  </div>
                  <p className="text-sm text-muted-foreground line-clamp-3">{t.description || "—"}</p>
                  <div className="mt-3 flex flex-wrap gap-2">
                    {(t.tags || []).slice(0, 4).map((x, i) => (<Badge key={i} variant="secondary" className="capitalize">{x}</Badge>))}
                  </div>
                  <div className="mt-4"><Button size="sm" variant="outline">Open</Button></div>
                </CardContent>
              </Card>
            </a>
          ))}
        </div>
      )}

      <div className="mt-8">
        <Link to="/categories"><Button variant="ghost">← Back to Categories</Button></Link>
      </div>
    </main>
  );
}
