import React, { useEffect, useMemo, useRef, useState } from "react";
import { Card, CardContent } from "./card";
import { Badge } from "./badge";

type Tool = { name: string; link?: string | null; icon?: string | null };
type Step = { title: string; tools: Tool[] };

type Props = {
  goal: string;
  steps: Step[];
  className?: string;
};

export default function PlanMindMap({ goal, steps, className }: Props) {
  const containerRef = useRef<HTMLDivElement | null>(null);
  const rootWrapperRef = useRef<HTMLDivElement | null>(null);
  const stepRefs = useRef<(HTMLDivElement | null)[]>([]);
  const [openIdx, setOpenIdx] = useState<number | null>(null);
  const [paths, setPaths] = useState<string[]>([]);

  const cleanSteps = useMemo(
    () => steps.filter((s) => s?.title && Array.isArray(s.tools)),
    [steps]
  );

  useEffect(() => {
    function recalc() {
      const c = containerRef.current;
      const root = rootWrapperRef.current;
      if (!c || !root) return;

      const cRect = c.getBoundingClientRect();
      const rRect = root.getBoundingClientRect();
      const rPoint = {
        x: rRect.right - cRect.left,
        y: rRect.top - cRect.top + rRect.height / 2,
      };

      const next: string[] = [];
      cleanSteps.forEach((_, i) => {
        const el = stepRefs.current[i];
        if (!el) return;
        const sRect = el.getBoundingClientRect();
        const sPoint = {
          x: sRect.left - cRect.left,
          y: sRect.top - cRect.top + sRect.height / 2,
        };
        const dx = Math.max(60, (sPoint.x - rPoint.x) / 2);
        const d = `M ${rPoint.x} ${rPoint.y}
                   C ${rPoint.x + dx} ${rPoint.y},
                     ${sPoint.x - dx} ${sPoint.y},
                     ${sPoint.x} ${sPoint.y}`;
        next.push(d);
      });
      setPaths(next);
    }

    recalc();
    const ro = new ResizeObserver(recalc);
    if (containerRef.current) ro.observe(containerRef.current);
    window.addEventListener("resize", recalc);
    const id = window.setInterval(recalc, 300);

    return () => {
      window.removeEventListener("resize", recalc);
      clearInterval(id);
      ro.disconnect();
    };
  }, [cleanSteps]);

  return (
    <div ref={containerRef} className={`relative w-full ${className ?? ""}`}>
      {/* connection lines */}
      <svg className="pointer-events-none absolute inset-0 w-full h-full">
        {paths.map((d, i) => (
          <path
            key={i}
            d={d}
            fill="none"
            stroke="rgb(203 213 225)" /* slate-300 */
            strokeWidth={2}
          />
        ))}
      </svg>

      <div className="grid grid-cols-[260px_1fr_320px] gap-8">
        {/* Goal */}
        <div ref={rootWrapperRef}>
          <Card className="rounded-2xl shadow-sm">
            <CardContent className="py-5">
              <div className="text-xs text-slate-500 mb-1">Goal</div>
              <div className="font-semibold leading-snug">{goal}</div>
            </CardContent>
          </Card>
        </div>

        {/* Steps */}
        <div className="space-y-4">
          {cleanSteps.map((step, i) => (
            <div
              key={`${i}-${step.title}`}
              ref={(el: HTMLDivElement | null) => {
                stepRefs.current[i] = el;
              }}
            >
              <button
                onClick={() => setOpenIdx((v) => (v === i ? null : i))}
                className="w-full text-left"
                title="Show tools for this step"
              >
                <Card
                  className={`rounded-2xl transition shadow-sm hover:shadow-md ${
                    openIdx === i ? "ring-2 ring-indigo-200" : ""
                  }`}
                >
                  <CardContent className="py-4">
                    <div className="flex items-center justify-between">
                      <div className="font-medium">{step.title}</div>
                      <Badge variant="secondary" className="text-[11px]">
                        {step.tools.length} tool
                        {step.tools.length === 1 ? "" : "s"}
                      </Badge>
                    </div>
                  </CardContent>
                </Card>
              </button>
            </div>
          ))}
        </div>

        {/* Tools panel */}
        <div>
          {openIdx !== null && cleanSteps[openIdx] && (
            <Card className="rounded-2xl shadow-sm sticky top-6">
              <CardContent className="py-5">
                <div className="text-xs text-slate-500 mb-2">
                  Tools for:{" "}
                  <span className="text-slate-700 font-medium">
                    {cleanSteps[openIdx].title}
                  </span>
                </div>

                <div className="flex flex-wrap gap-2">
                  {cleanSteps[openIdx].tools.map((t, i) => (
                    <ToolPill key={`${t.name}-${i}`} tool={t} />
                  ))}
                </div>

                {cleanSteps[openIdx].tools.length === 0 && (
                  <div className="text-sm text-slate-500">
                    No tools suggested.
                  </div>
                )}
              </CardContent>
            </Card>
          )}
        </div>
      </div>
    </div>
  );
}

function ToolPill({ tool }: { tool: Tool }) {
  const initial =
    typeof tool.name === "string" && tool.name.trim()
      ? tool.name.trim().charAt(0).toUpperCase()
      : "â€¢";

  const content = (
    <div className="inline-flex items-center gap-2 px-3 py-1.5 rounded-full border border-slate-200 bg-white hover:bg-slate-50 transition">
      {tool.icon ? (
        <img
          src={tool.icon}
          alt={tool.name}
          className="w-4 h-4 rounded"
          referrerPolicy="no-referrer"
          onError={(e) => {
            (e.currentTarget as HTMLImageElement).style.display = "none";
          }}
        />
      ) : (
        <div className="w-4 h-4 rounded bg-indigo-100 text-indigo-600 text-[10px] grid place-items-center">
          {initial}
        </div>
      )}
      <span className="text-sm">{tool.name}</span>
    </div>
  );

  return tool.link ? (
    <a href={tool.link} target="_blank" rel="noreferrer noopener" title={tool.name}>
      {content}
    </a>
  ) : (
    <span title={tool.name}>{content}</span>
  );
}
