// src/pages/Categories.tsx (или при теб: Categories.tsx в src/)
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { apiGet } from "../lib/api"; // нагласи пътя според твоята структура

type Category = { id: string; label: string; count: number };

export default function Categories() {
  const [items, setItems] = useState<Category[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    apiGet<Category[]>("/catalog/categories")
      .then(setItems)
      .finally(() => setLoading(false));
  }, []);

  if (loading) return <div className="p-8">Зареждане…</div>;

  return (
    <div className="max-w-6xl mx-auto p-6 grid gap-4 grid-cols-1 sm:grid-cols-2 lg:grid-cols-3">
      {items.map((c) => (
        <Link key={c.id} to={`/categories/${encodeURIComponent(c.id)}`}>
          <div className="rounded-lg border bg-white p-5 hover:shadow">
            <div className="text-sm text-slate-500">{c.label}</div>
            <div className="text-2xl font-semibold">{c.count}</div>
          </div>
        </Link>
      ))}
    </div>
  );
}
