import React, { useEffect, useRef, useState, useCallback } from 'react';
import { Search, Star, MessageCircle, Zap, Sparkles, History, X, ArrowLeft, Grid3x3, Heart, User, Menu, Wand2, ExternalLink } from 'lucide-react';

import { Button } from './components/ui/button';
import { Input } from './components/ui/input';
import { Badge } from './components/ui/badge';
import { Card, CardContent } from './components/ui/card';

import { openOAuthPopup, fetchMe, refreshSession, logout } from './auth';
import PlanMindMap from './components/ui/PlanMindMap';

/* ---------- API base ---------- */
const API_BASE =
  (import.meta as any).env?.VITE_API_BASE ||
  (window as any).__API_BASE__ ||
  'http://127.0.0.1:8000';

/* ---------- Types matching backend ---------- */
type Tool = { name: string; link?: string | null; icon?: string | null };
type Group = { title: string; tools: Tool[] };
type PlanItem = { task: string; tools: Tool[] };
type PlanResponse = { goal: string; plan: PlanItem[]; groups?: Group[] };

type CurrentUser = {
  id: number;
  email: string;
  name?: string | null;
  picture?: string | null;
} | null;

/* ---------- Category types (from backend /catalog) ---------- */
type CategorySummary = {
  id: string;                 // backend capability id, used in /catalog/categories/{id}
  label: string;              // localized or canonical title
  description?: string | null;
  emoji?: string | null;
  gradient?: string | null;
  count?: number | null;
};

type CategoryToolsResponse = {
  id: string;
  label: string;
  tools: Tool[];
};

/* ---------- Language and Localization ---------- */
type Language = 'en' | 'bg';

const translations = {
  en: {
    discover: 'Discover',
    categories: 'Categories',
    learn: 'Learn',
    favorites: 'Favorites',
    signIn: 'Sign In',
    profile: 'Profile',
    signOut: 'Sign Out',

    heroTitle: 'Discover AI tools that',
    heroTitleHighlight: 'actually help',
    heroSubtitle: 'From writing essays to creating art, find the perfect AI tools for your goals.',
