  /* ---------- Categories View (dynamic from backend) ---------- */
  function CategoriesView() {
    const [search, setSearch] = useState('');
    const [loadingCats, setLoadingCats] = useState(true);
    const [categories, setCategories] = useState<CategorySummary[]>([]);
    const [selected, setSelected] = useState<CategorySummary | null>(null);
    const [catTools, setCatTools] = useState<Tool[]>([]);
    const [loadingTools, setLoadingTools] = useState(false);
    const [catError, setCatError] = useState<string | null>(null);

    // Load categories from backend
    useEffect(() => {
      let mounted = true;
      (async () => {
        try {
          setLoadingCats(true);
          setCatError(null);
          const tstamp = Date.now();
          const res = await fetch(`${API_BASE}/catalog/categories?t=${tstamp}`, {
            headers: { 'Cache-Control': 'no-cache' }
          });
          if (!res.ok) throw new Error(`HTTP ${res.status}`);
          const data: CategorySummary[] = await res.json();
          if (mounted) setCategories(data);
        } catch (e) {
          console.error('Load categories error:', e);
          if (mounted) setCatError(translations[language].errorGeneric);
        } finally {
          if (mounted) setLoadingCats(false);
        }
      })();
      return () => { mounted = false; };
    }, [language]);

    // Load tools by category
    const openCategory = useCallback(async (cat: CategorySummary) => {
      setSelected(cat);
      setCatTools([]);
      setLoadingTools(true);
      setCatError(null);
      try {
        const tstamp = Date.now();
        const res = await fetch(`${API_BASE}/catalog/categories/${encodeURIComponent(cat.id)}?t=${tstamp}`, {
          headers: { 'Cache-Control': 'no-cache' }
        });
        if (!res.ok) throw new Error(`HTTP ${res.status}`);
        const data: CategoryToolsResponse = await res.json();
        setCatTools(data.tools || []);
      } catch (e) {
        console.error('Load tools error:', e);
        setCatError(translations[language].errorGeneric);
      } finally {
        setLoadingTools(false);
      }
    }, [language]);

    const filtered = categories.filter(c =>
      (c.label || '').toLowerCase().includes(search.toLowerCase()) ||
      (c.description || '').toLowerCase().includes(search.toLowerCase())
    );

    return (
      <main className="max-w-6xl mx-auto px-4 py-8 pb-24 md:pb-12">
        {/* Header */}
        <div className="flex items-center justify-between mb-8">
          <div className="flex items-center gap-3">
            <button onClick={() => {
              if (selected) { setSelected(null); setCatTools([]); setCatError(null); }
              else { goHome(); }
            }} className="inline-flex items-center gap-2 text-slate-600 hover:text-blue-600 transition-colors p-2 -ml-2 rounded-xl">
              <ArrowLeft className="w-4 h-4" />
              <span className="font-medium">{t('back')}</span>
            </button>
            <div className="w-px h-6 bg-slate-300" />
            <div>
              <h1 className="text-3xl text-slate-800 font-bold">
                {selected ? (selected.label || t('categoriesTitle')) : t('categoriesTitle')}
              </h1>
              <p className="text-slate-600 text-sm mt-1">
                {selected ? t('toolsInCategory') : t('categoriesSubtitle')}
              </p>
            </div>
          </div>
        </div>

        {/* Top search (only in list mode) */}
        {!selected && (
          <div className="mb-8">
            <div className="relative max-w-md">
              <Search className="absolute left-4 top-1/2 transform -translate-y-1/2 text-slate-400 w-5 h-5" />
              <Input
                placeholder={t('searchCategories')}
                value={search}
                onChange={(e) => setSearch(e.target.value)}
                className="pl-12 bg-white/80 border-slate-200/50 h-12 rounded-2xl backdrop-blur-sm font-medium"
              />
            </div>
          </div>
        )}

        {/* Categories grid */}
        {!selected && (
          <>
            {loadingCats ? (
              <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6">
                {Array.from({ length: 9 }).map((_, i) => (
                  <div key={i} className="h-40 bg-slate-100 rounded-3xl animate-pulse" />
                ))}
              </div>
            ) : catError ? (
              <div className="text-red-600 font-medium mb-6 p-4 bg-red-50 rounded-2xl border border-red-200">
                {catError}
              </div>
            ) : (
              <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6">
                {filtered.map((category) => (
                  <Card
                    key={category.id}
                    className="group border-slate-200/50 hover:shadow-2xl transition-all duration-300 hover:-translate-y-2 bg-white/90 backdrop-blur-sm cursor-pointer overflow-hidden"
                    onClick={() => openCategory(category)}
                  >
                    <CardContent className="p-6 relative">
                      {/* Background gradient must not block clicks */}
                      <div className={`absolute inset-0 bg-gradient-to-br ${category.gradient || 'from-blue-500 to-indigo-600'} opacity-5 group-hover:opacity-10 transition-opacity duration-300 pointer-events-none`} />
                      <div className="relative">
                        <div className="flex items-start justify-between mb-4">
                          <div className={`w-16 h-16 rounded-3xl bg-gradient-to-br ${category.gradient || 'from-blue-500 to-indigo-600'} flex items-center justify-center shadow-lg group-hover:scale-110 transition-transform duration-300`}>
                            <span className="text-2xl">{category.emoji || 'üß≠'}</span>
                          </div>
                          <div className="text-right">
                            {typeof category.count === 'number' && (
                              <>
                                <div className="text-xs text-slate-500">{category.count}</div>
                                <div className="text-xs text-slate-400">{t('toolsInCategory')}</div>
                              </>
                            )}
                          </div>
                        </div>

                        <h3 className="text-xl font-bold text-slate-800 mb-2 group-hover:text-slate-900 transition-colors">
                          {category.label}
                        </h3>

                        {category.description && (
                          <p className="text-sm text-slate-600 mb-4 leading-relaxed">
                            {category.description}
                          </p>
                        )}

                        <div className="flex items-center justify-between">
                          <Badge
                            variant="secondary"
                            className={`text-xs font-medium px-3 py-1 bg-gradient-to-r ${category.gradient || 'from-blue-500 to-indigo-600'} text-white border-0`}
                          >
                            {t('browseCategory')}
                          </Badge>
                          <ArrowLeft className="w-4 h-4 text-slate-400 rotate-180 group-hover:translate-x-1 transition-transform duration-300" />
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            )}

            {/* Stats */}
            {!loadingCats && !catError && (
              <div className="mt-12 text-center">
                <div className="inline-flex items-center gap-6 bg-white/80 border border-slate-200/50 rounded-2xl px-6 py-4 backdrop-blur-sm">
                  <div className="text-center">
                    <div className="text-2xl font-bold text-slate-800">{filtered.length}</div>
                    <div className="text-xs text-slate-500">{t('totalCategories')}</div>
                  </div>
                  <div className="w-px h-8 bg-slate-300" />
                  <div className="text-center">
                    <div className="text-2xl font-bold text-slate-800">
                      {filtered.reduce((total, cat) => total + (cat.count || 0), 0)}
                    </div>
                    <div className="text-xs text-slate-500">{t('totalTools')}</div>
                  </div>
                </div>
              </div>
            )}
          </>
        )}

        {/* Selected category tools */}
        {selected && (
          <>
            {loadingTools ? (
              <div className="text-blue-600 font-medium mb-6 p-4 bg-blue-50 rounded-2xl border border-blue-200">
                {t('loadingTools')}
              </div>
            ) : catError ? (
              <div className="text-red-600 font-medium mb-6 p-4 bg-red-50 rounded-2xl border border-red-200">
                {catError}
              </div>
            ) : catTools.length === 0 ? (
              <div className="text-center py-16">
                <div className="w-20 h-20 bg-slate-100 rounded-3xl flex items-center justify-center mx-auto mb-6">
                  <Search className="w-10 h-10 text-slate-400" />
                </div>
                <h3 className="text-slate-800 mb-3 font-bold text-xl">{t('noToolsInCategory')}</h3>
              </div>
            ) : (
              <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6">
                {catTools.map((tool) => (
                  <Card key={`${selected.id}-${tool.name}`} className="border-slate-200/50 hover:shadow-xl transition-all duration-300 hover:-translate-y-1 bg-white/90 backdrop-blur-sm group">
                    <CardContent className="p-6">
                      <div className="flex items-start justify-between mb-4">
                        <div className="w-14 h-14 rounded-2xl overflow-hidden bg-gradient-to-br from-blue-400 to-indigo-500 flex items-center justify-center shadow-lg group-hover:scale-105 transition-transform duration-300">
                          {tool.icon ? (
                            <img
                              src={tool.icon}
                              alt={tool.name}
                              className="w-8 h-8 object-contain"
                              loading="lazy"
                              referrerPolicy="no-referrer"
                              onError={(e) => {
                                const img = e.currentTarget as HTMLImageElement;
                                img.onerror = null;
                                img.style.display = 'none';
                              }}
                            />
                          ) : (
                            <Sparkles className="w-7 h-7 text-white" />
                          )}
                        </div>
                        <button
                          onClick={() => toggleFav(tool)}
                          className={`flex items-center gap-1 px-3 py-1.5 rounded-xl border transition-all ${
                            favs.has(tool.name)
                              ? 'bg-yellow-100 border-yellow-200 text-yellow-700 hover:bg-yellow-200'
                              : 'bg-white border-slate-200 text-slate-500 hover:bg-slate-50 hover:border-slate-300'
                          }`}
                          title={favs.has(tool.name) ? t('removeFromFavorites') : t('addToFavorites')}
                        >
                          <Star className={`w-4 h-4 ${favs.has(tool.name) ? 'fill-current' : ''}`} />
                          <span className="text-sm font-medium hidden sm:inline">
                            {favs.has(tool.name) ? translations[language].saved : translations[language].save}
                          </span>
                        </button>
                      </div>

                      <h3 className="text-slate-800 mb-3 font-bold text-lg leading-tight">{tool.name}</h3>

                      <div className="flex gap-2 mb-5">
                        <Badge variant="secondary" className="text-xs bg-blue-100 text-blue-700 font-medium px-2 py-1">
                          {t('aiTool')}
                        </Badge>
                      </div>

                      {tool.link ? (
                        <a
                          href={tool.link}
                          target="_blank"
                          rel="noreferrer noopener"
                          className="inline-flex items-center justify-center w-full gap-2 text-center bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 text-white rounded-2xl h-11 font-medium shadow-lg shadow-blue-500/25 transition-all duration-200"
                        >
                          <ExternalLink className="w-4 h-4" />
                          {t('open')}
                        </a>
                      ) : (
                        <Button disabled className="w-full rounded-2xl h-11 bg-slate-100 text-slate-400">
                          {t('noLink')}
                        </Button>
                      )}
                    </CardContent>
                  </Card>
                ))}
              </div>
            )}
          </>
        )}
      </main>
    );
  }
}
