import React, { useMemo, useState } from 'react';
import { ChevronDown, ChevronUp, Lightbulb, Copy, Check } from 'lucide-react';
import { Badge } from './badge';
import { Button } from './button';

type Tool = { name: string; link?: string | null; icon?: string | null };
type Group = { title: string; tools: Tool[] };

type Language = 'en' | 'bg';

type PlanMindMapProps = {
  goal: string;
  steps: Group[];
  onOpenTool: (tool: Tool) => void;

  /** i18n access from App – keep it stringly-typed to avoid coupling */
  t: (key: string) => string;
  language: Language;

  /** Build the prompt that appears at the top of each expanded step */
  buildPrompt: (args: { goal: string; stepTitle: string; tools: Tool[] }) => string;
};

export default function PlanMindMap({
  goal,
  steps,
  onOpenTool,
  t,
  language,
  buildPrompt,
}: PlanMindMapProps) {
  const [openIndex, setOpenIndex] = useState<number | null>(0); // open first by default
  const [copiedIndex, setCopiedIndex] = useState<number | null>(null);

  const headerTitle = useMemo(() => goal || (language === 'bg' ? 'Моята цел' : 'My goal'), [goal, language]);

  const copy = async (text: string, i: number) => {
    try {
      await navigator.clipboard.writeText(text);
      setCopiedIndex(i);
      setTimeout(() => setCopiedIndex(null), 1800);
    } catch {
      alert(t('copyFailed'));
    }
  };

  return (
    <div className="space-y-6">
      {/* Goal header */}
      <div className="text-center">
        <div className="inline-flex items-center gap-3 bg-white/80 border border-slate-200 rounded-2xl px-4 py-3 shadow-sm">
          <span className="text-slate-700">{headerTitle}</span>
          {goal && <Badge variant="secondary" className="bg-blue-50 text-blue-700 border-blue-200">{goal}</Badge>}
        </div>
      </div>

      {/* Steps */}
      <div className="space-y-6">
        {steps.map((step, i) => {
          const isOpen = openIndex === i;
          const prompt = buildPrompt({ goal, stepTitle: step.title, tools: step.tools });

          return (
            <div key={`${step.title}-${i}`} className={`rounded-2xl border ${isOpen ? 'border-blue-200 bg-white' : 'border-slate-200 bg-white/80'} shadow-sm`}>
              {/* Row header */}
              <div className="flex items-center gap-3 px-4 sm:px-5 py-4">
                <div className="w-8 h-8 rounded-xl bg-gradient-to-br from-blue-500 to-indigo-600 text-white font-semibold flex items-center justify-center">{i + 1}</div>

                <div className="flex-1">
                  <div className="font-semibold text-slate-800">{step.title}</div>
                  <div className="text-xs text-slate-500">{step.tools.length} {t('recommendedTools')}</div>
                </div>

                {/* AI assistant action (orange) */}
                <Button
                  onClick={() => setOpenIndex(isOpen ? null : i)}
                  className="bg-gradient-to-r from-amber-400 to-orange-500 hover:from-amber-500 hover:to-orange-600 text-white h-9 px-3 rounded-xl flex items-center gap-2 shadow-sm"
                >
                  <Lightbulb className="w-4 h-4" />
                  <span className="text-sm font-medium">{t('assistantSuggestion')}</span>
                  {isOpen ? <ChevronUp className="w-4 h-4" /> : <ChevronDown className="w-4 h-4" />}
                </Button>
              </div>

              {/* Expanded content */}
              {isOpen && (
                <div className="px-4 sm:px-5 pb-5 space-y-5">
                  {/* PROMPT at the very top of the expanded section */}
                  <div className="bg-gradient-to-r from-amber-50 to-orange-50 border border-amber-200/60 rounded-xl p-4">
                    <div className="flex items-start gap-3">
                      <div className="w-9 h-9 rounded-lg bg-gradient-to-br from-amber-400 to-orange-500 text-white flex items-center justify-center flex-shrink-0">
                        <Lightbulb className="w-4 h-4" />
                      </div>
                      <div className="flex-1">
                        <div className="flex items-center gap-2 mb-2">
                          <span className="font-semibold text-amber-800">{t('assistantSuggestion')}</span>
                          <Badge variant="secondary" className="text-xs bg-amber-100 text-amber-700 border-amber-200">
                            {t('copyPasteReady')}
                          </Badge>
                        </div>
                        <div className="bg-white/80 border border-amber-200 rounded-lg p-3 text-sm text-slate-700">
                          {prompt}
                        </div>
                        <div className="mt-2 flex items-center justify-between">
                          <span className="text-xs text-amber-700">{t('promptHint')}</span>
                          <Button
                            variant="outline"
                            onClick={() => copy(prompt, i)}
                            className={`h-8 px-3 rounded-lg border-amber-200 ${copiedIndex === i ? 'bg-green-50 border-green-200 text-green-700' : 'text-amber-700 hover:bg-amber-50'}`}
                          >
                            {copiedIndex === i ? <Check className="w-4 h-4 mr-1" /> : <Copy className="w-4 h-4 mr-1" />}
                            <span className="text-xs font-medium">{copiedIndex === i ? t('copied') : t('copy')}</span>
                          </Button>
                        </div>
                      </div>
                    </div>
                  </div>

                  {/* Tools grid for that step */}
                  <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-4">
                    {step.tools.map((tool) => (
                      <button
                        key={`${step.title}-${tool.name}`}
                        onClick={() => tool.link && onOpenTool(tool)}
                        className={`flex items-center gap-3 rounded-xl border p-3 text-left transition ${
                          tool.link ? 'bg-white border-slate-200 hover:shadow-md hover:-translate-y-[2px]' : 'bg-slate-50 border-slate-200 opacity-70 cursor-default'
                        }`}
                      >
                        <div className="w-10 h-10 rounded-xl bg-gradient-to-br from-blue-400 to-indigo-500 flex items-center justify-center overflow-hidden">
                          {tool.icon ? (
                            <img src={tool.icon} alt={tool.name} className="w-6 h-6 object-contain" referrerPolicy="no-referrer" />
                          ) : (
                            <span className="text-white text-sm font-semibold">{tool.name.slice(0, 1)}</span>
                          )}
                        </div>
                        <div className="flex-1 min-w-0">
                          <div className="font-medium text-slate-800 truncate">{tool.name}</div>
                          <div className="text-xs text-slate-500 truncate">
                            {tool.link ? (language === 'bg' ? 'Налично' : 'Available') : (language === 'bg' ? 'Няма линк' : 'No link')}
                          </div>
                        </div>
                        <Badge variant="secondary" className="text-[10px] bg-blue-50 text-blue-700 border-blue-200">
                          {t('aiTool')}
                        </Badge>
                      </button>
                    ))}
                  </div>
                </div>
              )}
            </div>
          );
        })}
      </div>
    </div>
  );
}
