import { useEffect, useMemo, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { Card, CardContent } from "../components/ui/card";
import { Badge } from "../components/ui/badge";
import { Button } from "../components/ui/button";
import { Skeleton } from "../components/ui/skeleton";
import { Separator } from "../components/ui/separator";
import { ToggleGroup, ToggleGroupItem } from "../components/ui/toggle-group";
import { ArrowLeft, ExternalLink } from "lucide-react";

type Tool = {
  id?: number | string;
  name: string;
  description?: string;
  tags?: string[];
  icon?: string | null;
  link?: string | null;
  rating?: number | null;
};

const API_BASE =
  (import.meta as any).env?.VITE_API_BASE ||
  (window as any).__API_BASE__ ||
  "http://127.0.0.1:8000";

export default function CategoryView() {
  const { cap = "" } = useParams();
  const [loading, setLoading] = useState(true);
  const [tools, setTools] = useState<Tool[]>([]);
  const [filter, setFilter] = useState<string | null>(null);

  useEffect(() => {
    let cancelled = false;
    setLoading(true);

    (async () => {
      try {
        // ✅ правилният бекенд ендпойнт
        const r = await fetch(
          `${API_BASE}/catalog/tools?cap=${encodeURIComponent(cap || "")}`
        );
        const data = await r.json();

        // API shape: { total, items: [...] }
        const arr: Tool[] = (data?.items || data || []).map((t: any) => ({
          id: t.id ?? t.name,
          name: t.name,
          description: t.description ?? "",
          tags: t.tags ?? [],
          icon: t.icon ?? t.icon_url ?? null,
          link: t.link ?? t.links?.website ?? null,
          rating: t.rating ?? null,
        }));

        if (!cancelled) {
          setTools(arr);
          setLoading(false);
        }
      } catch (e) {
        console.error("CategoryView fetch failed", e);
        if (!cancelled) {
          setTools([]);
          setLoading(false);
        }
      }
    })();

    return () => {
      cancelled = true;
    };
  }, [cap]);

  const shown = useMemo(() => {
    if (!filter) return tools;
    const f = filter.toLowerCase();
    return tools.filter((t) =>
      (t.tags || []).some((x) => (x || "").toLowerCase().includes(f))
    );
  }, [tools, filter]);

  return (
    <main className="mx-auto max-w-7xl px-4 py-8">
      <div className="flex items-end justify-between gap-4">
        <div className="flex items-center gap-3">
          <Link
            to="/categories"
            className="inline-flex items-center gap-2 text-slate-600 hover:text-blue-600 transition-colors p-2 -ml-2 rounded-xl"
          >
            <ArrowLeft className="w-4 h-4" />
            <span className="font-medium">Назад</span>
          </Link>
          <div>
            <h1 className="text-2xl md:text-3xl font-bold tracking-tight break-words">
              {cap}
            </h1>
            <p className="text-muted-foreground mt-1">
              Инструменти в тази категория. Използвай филтрите, за да прецизираш
              резултатите.
            </p>
          </div>
        </div>

        <ToggleGroup
          type="single"
          value={filter ?? ""}
          onValueChange={(v) => setFilter(v || null)}
          className="bg-muted rounded-xl p-1"
        >
          <ToggleGroupItem value="" aria-label="All">
            Всички
          </ToggleGroupItem>
          <ToggleGroupItem value="free" aria-label="Free">
            Безплатни
          </ToggleGroupItem>
          <ToggleGroupItem value="trending" aria-label="Trending">
            Тренд
          </ToggleGroupItem>
          <ToggleGroupItem value="new" aria-label="New">
            Нови
          </ToggleGroupItem>
        </ToggleGroup>
      </div>

      <Separator className="my-6" />

      {loading ? (
        <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-4">
          {Array.from({ length: 9 }).map((_, i) => (
            <Skeleton key={i} className="h-40 rounded-2xl" />
          ))}
        </div>
      ) : shown.length === 0 ? (
        <div className="text-sm text-muted-foreground">Няма инструменти.</div>
      ) : (
        <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-4">
          {shown.map((t) => (
            <a
              key={t.id}
              href={t.link || "#"}
              target="_blank"
              rel="noreferrer"
              className="group"
              onClick={(e) => {
                if (!t.link) e.preventDefault();
              }}
            >
              <Card className="rounded-2xl h-full hover:shadow-md transition">
                <CardContent className="pt-5">
                  <div className="flex items-start gap-3">
                    <div className="w-10 h-10 rounded-xl bg-slate-100 flex items-center justify-center overflow-hidden shrink-0">
                      {t.icon ? (
                        <img
                          src={t.icon}
                          alt={t.name}
                          className="w-10 h-10 object-cover"
                          loading="lazy"
                        />
                      ) : (
                        <span className="text-slate-400 text-sm">AI</span>
                      )}
                    </div>
                    <div className="min-w-0">
                      <div className="flex items-center gap-2">
                        <h3 className="font-semibold truncate">{t.name}</h3>
                        {t.rating != null && (
                          <Badge variant="secondary">{t.rating.toFixed(2)}</Badge>
                        )}
                      </div>
                      <p className="text-sm text-slate-600 line-clamp-2 mt-0.5">
                        {t.description || "AI Tool"}
                      </p>
                      <div className="mt-2 flex flex-wrap gap-1">
                        {(t.tags || []).slice(0, 4).map((tag, i) => (
                          <Badge key={i} variant="outline">
                            {tag}
                          </Badge>
                        ))}
                      </div>
                      <div className="mt-3">
                        <Button
                          size="sm"
                          variant={t.link ? "default" : "secondary"}
                          className="rounded-xl"
                        >
                          Отвори инструмент <ExternalLink className="w-4 h-4 ml-2" />
                        </Button>
                      </div>
                    </div>
                  </div>
                </CardContent>
              </Card>
            </a>
          ))}
        </div>
      )}
    </main>
  );
}
