import React, { useEffect, useLayoutEffect, useMemo, useRef, useState } from "react";
import { Card, CardContent } from "./card";
import { Badge } from "./badge";
import { ExternalLink, ChevronRight } from "lucide-react";

type Tool = { name: string; link?: string | null; icon?: string | null };
type Step = { title: string; tools: Tool[] };

type Props = {
  goal: string;
  steps: Step[];
  className?: string;
  onOpenTool?: (tool: Tool) => void;
};

type PathSeg = { d: string };

export default function PlanMindMap({ goal, steps, className, onOpenTool }: Props) {
  const cleanSteps = useMemo(
    () => (steps || []).filter((s) => s && s.title && Array.isArray(s.tools)),
    [steps]
  );

  const wrapperRef = useRef<HTMLDivElement | null>(null);
  const goalRef = useRef<HTMLDivElement | null>(null);
  const stepRefs = useRef<(HTMLDivElement | null)[]>([]);
  const toolRefs = useRef<(HTMLDivElement | null)[]>([]);

  const [activeIdx, setActiveIdx] = useState<number | null>(null);
  const [goalToSteps, setGoalToSteps] = useState<PathSeg[]>([]);
  const [stepToTools, setStepToTools] = useState<PathSeg[]>([]);

  // reset tool refs при смяна на активната стъпка
  useEffect(() => {
    toolRefs.current = [];
  }, [activeIdx]);

  useLayoutEffect(() => {
    function compute() {
      const wrap = wrapperRef.current;
      const goalEl = goalRef.current;
      if (!wrap || !goalEl) return;

      const wRect = wrap.getBoundingClientRect();
      const gRect = goalEl.getBoundingClientRect();
      const gX = gRect.right - wRect.left;
      const gY = gRect.top - wRect.top + gRect.height / 2;

      // Goal → Steps
      const gs: PathSeg[] = [];
      cleanSteps.forEach((_, i) => {
        const st = stepRefs.current[i];
        if (!st) return;
        const sRect = st.getBoundingClientRect();
        const sX = sRect.left - wRect.left;
        const sY = sRect.top - wRect.top + sRect.height / 2;
        gs.push({ d: cubic(gX, gY, sX, sY, 64) });
      });
      setGoalToSteps(gs);

      // Active Step → Tools
      const tt: PathSeg[] = [];
      if (typeof activeIdx === "number") {
        const st = stepRefs.current[activeIdx];
        if (st) {
          const sRect = st.getBoundingClientRect();
          const startX = sRect.right - wRect.left;
          const startY = sRect.top - wRect.top + sRect.height / 2;

          toolRefs.current.forEach((el) => {
            if (!el) return;
            const tRect = el.getBoundingClientRect();
            const endX = tRect.left - wRect.left;
            const endY = tRect.top - wRect.top + tRect.height / 2;
            tt.push({ d: cubic(startX, startY, endX, endY, 48) });
          });
        }
      }
      setStepToTools(tt);
    }

    compute();
    const onResize = () => compute();
    window.addEventListener("resize", onResize);
    const id = window.setInterval(compute, 100);
    return () => {
      window.removeEventListener("resize", onResize);
      window.clearInterval(id);
    };
  }, [cleanSteps, activeIdx]);

  function cubic(x1: number, y1: number, x2: number, y2: number, k: number) {
    return `M ${x1} ${y1} C ${x1 + k} ${y1}, ${x2 - k} ${y2}, ${x2} ${y2}`;
  }

  return (
    <div
      ref={wrapperRef}
      className={`relative overflow-visible rounded-2xl bg-slate-50/70 border border-slate-200 p-4 md:p-6 ${className ?? ""}`}
    >
      {/* SVG линии */}
      <svg className="pointer-events-none absolute inset-0 z-0" width="100%" height="100%">
        {goalToSteps.map((p, i) => (
          <path key={`gs-${i}`} d={p.d} stroke="rgba(148,163,184,0.45)" strokeWidth={3} fill="none" />
        ))}
        {stepToTools.map((p, i) => (
          <path key={`st-${i}`} d={p.d} stroke="rgba(59,130,246,0.55)" strokeWidth={3} fill="none" />
        ))}
      </svg>

      {/* 3 колони: Goal | Steps | място за ToolPill-ите */}
      <div className="relative grid grid-cols-[260px_1fr_auto] gap-6">
        {/* Goal */}
        <div className="pt-10">
          <div ref={goalRef}>
            <Card className="rounded-2xl shadow-sm">
              <CardContent className="py-4">
                <div className="text-xs text-slate-500 mb-1">Goal</div>
                <div className="font-semibold leading-snug">{goal || "—"}</div>
              </CardContent>
            </Card>
          </div>
        </div>

        {/* Steps */}
        <div className="space-y-4 relative">
          {cleanSteps.map((step, i) => (
            <div
              key={`step-${i}`}
              ref={(el: HTMLDivElement | null) => {
                stepRefs.current[i] = el;
              }}
              className="relative"
            >
              <Card
                className={`rounded-2xl transition shadow-sm cursor-pointer ${
                  i === activeIdx ? "ring-2 ring-blue-500/40" : "hover:shadow-md"
                }`}
                onClick={() => setActiveIdx((prev) => (prev === i ? null : i))}
              >
                <CardContent className="py-5 pr-16">
                  <div className="font-semibold leading-snug">{step.title}</div>
                </CardContent>
                <div className="absolute right-3 top-1/2 -translate-y-1/2">
                  <Badge className="rounded-full px-3 py-1 text-xs bg-slate-100 text-slate-600">
                    {step.tools?.length || 0} tools
                  </Badge>
                </div>
              </Card>

              {activeIdx === i && (
                <div className="absolute left-[calc(100%+1.5rem)] top-1/2 -translate-y-1/2 z-10">
                  <div className="flex flex-col gap-3">
                    {step.tools.map((t, ti) => (
                      <ToolPill
                        key={`tool-${ti}`}
                        tool={t}
                        setRef={(el: HTMLDivElement | null) => {
                          toolRefs.current[ti] = el;
                        }}
                        onClick={() => {
                          onOpenTool?.(t);
                          if (t.link) window.open(t.link, "_blank", "noopener,noreferrer");
                        }}
                      />
                    ))}
                  </div>
                </div>
              )}
            </div>
          ))}
        </div>

        <div className="w-[180px]" />
      </div>

      <div className="mt-4 flex items-center gap-2 text-xs text-slate-500">
        <ChevronRight className="w-3.5 h-3.5" />
        Click a step to see recommended tools.
      </div>
    </div>
  );
}

function ToolPill({
  tool,
  setRef,
  onClick,
}: {
  tool: Tool;
  setRef: (el: HTMLDivElement | null) => void;
  onClick: () => void;
}) {
  return (
    <div
      ref={(el) => setRef(el)}
      onClick={onClick}
      className="group select-none inline-flex items-center gap-2 rounded-xl border border-slate-200 bg-white px-3 py-2 shadow-sm hover:shadow-md cursor-pointer"
      title={tool.name}
    >
      {tool.icon ? (
        <img
          src={tool.icon}
          alt=""
          className="w-5 h-5 rounded-sm object-contain"
          referrerPolicy="no-referrer"
          onError={(e) => {
            const img = e.currentTarget as HTMLImageElement;
            img.onerror = null;
            img.src = "/icons/default-tool.svg";
          }}
        />
      ) : (
        <div className="w-5 h-5 rounded-sm bg-slate-200" />
      )}
      <span className="text-sm text-slate-700 font-medium">{tool.name}</span>
      <ExternalLink className="w-3.5 h-3.5 text-slate-400 group-hover:text-slate-600" />
    </div>
  );
}
