// src/pages/CategoryView.tsx
import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import { apiGet } from "../lib/api";

type Tool = { id: number; name: string; description?: string; links?: Record<string, string> };
type Resp = { id: string; tools: Tool[] };

export default function CategoryView() {
  const { cap } = useParams<{ cap: string }>();
  const [data, setData] = useState<Resp | null>(null);

  useEffect(() => {
    if (!cap) return;
    apiGet<Resp>(`/catalog/categories/${encodeURIComponent(cap)}`).then(setData);
  }, [cap]);

  if (!data) return <div className="p-8">Зареждане…</div>;

  return (
    <div className="max-w-6xl mx-auto p-6">
      <h1 className="text-2xl font-bold mb-4">{data.id}</h1>
      <div className="grid gap-4 grid-cols-1 sm:grid-cols-2 lg:grid-cols-3">
        {data.tools.map((t) => (
          <div key={t.id} className="rounded-lg border bg-white p-5">
            <div className="font-semibold">{t.name}</div>
            <p className="text-sm text-slate-600 line-clamp-3">{t.description}</p>
            <div className="mt-3 flex gap-3">
              <Link className="text-blue-600 hover:underline" to={`/tools/${t.id}`}>Детайли</Link>
              {t.links?.website && (
                <a className="text-slate-600 hover:underline" href={t.links.website} target="_blank" rel="noreferrer">
                  Уебсайт
                </a>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
