  // ---------- Category Detail (loads tools from backend) ----------
  function CategoryDetailView({ cap }: { cap: string }) {
    const [loading, setLoading] = useState(true);
    const [tools, setTools] = useState<Tool[]>([]);
    const [filter, setFilter] = useState<string | null>(null);

    useEffect(() => {
      let cancelled = false;
      setLoading(true);
      (async () => {
        try {
          const r = await fetch(`${API_BASE}/catalog/categories/${encodeURIComponent(cap)}`);
          const data = await r.json();
          const arr: any[] = data?.tools || data || [];
          if (!cancelled) {
            setTools(
              arr.map((t: any) => ({
                name: t.name,
                link: t.link ?? t.links?.website ?? null,
                icon: t.icon ?? t.icon_url ?? null,
                description: t.description ?? '',
                tags: t.tags ?? [],
              }))
            );
            setLoading(false);
          }
        } catch {
          if (!cancelled) { setTools([]); setLoading(false); }
        }
      })();
      return () => { cancelled = true; };
    }, [cap]);

    const shown = !filter
      ? tools
      : tools.filter((t) => (t.tags || []).some((x) => (x || '').toLowerCase().includes(filter.toLowerCase())));

    return (
      <main className="max-w-6xl mx-auto px-4 py-8 pb-24 md:pb-12">
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center gap-3">
            <button onClick={goCategories} className="inline-flex items-center gap-2 text-slate-600 hover:text-blue-600 transition-colors p-2 -ml-2 rounded-xl">
              <ArrowLeft className="w-4 h-4" />
              <span className="font-medium">{language === 'bg' ? '–ù–∞–∑–∞–¥' : 'Back'}</span>
            </button>
            <div className="w-px h-6 bg-slate-300" />
            <h1 className="text-2xl md:text-3xl font-bold tracking-tight break-words text-slate-800">
              {cap}
            </h1>
          </div>

          {/* Simple filter buttons */}
          <div className="bg-slate-100 rounded-xl p-1 flex gap-1">
            {[
              { val: null, label: t('all') },
              { val: 'free', label: language === 'bg' ? '–ë–µ–∑–ø–ª–∞—Ç–Ω–∏' : 'Free' },
              { val: 'trending', label: language === 'bg' ? '–¢—Ä–µ–Ω–¥' : 'Trending' },
              { val: 'new', label: language === 'bg' ? '–ù–æ–≤–∏' : 'New' },
            ].map((b) => (
              <button
                key={String(b.val)}
                onClick={() => setFilter(b.val)}
                className={`px-3 py-1.5 rounded-lg text-sm font-medium ${
                  filter === b.val || (b.val === null && filter === null)
                    ? 'bg-white shadow text-slate-800'
                    : 'text-slate-600 hover:text-slate-800'
                }`}
              >
                {b.label}
              </button>
            ))}
          </div>
        </div>

        {/* Grid */}
        {loading ? (
          <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-4">
            {Array.from({ length: 9 }).map((_, i) => (<div key={i} className="h-40 rounded-2xl bg-slate-100 animate-pulse" />))}
          </div>
        ) : shown.length === 0 ? (
          <div className="text-sm text-slate-500">{language === 'bg' ? '–ù—è–º–∞ –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–∏.' : 'No tools found.'}</div>
        ) : (
          <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-4">
            {shown.map((t) => (
              <a key={t.name} href={t.link || '#'} target="_blank" rel="noreferrer" className="group">
                <Card className="rounded-2xl h-full hover:shadow-md transition">
                  <CardContent className="pt-5">
                    <div className="flex items-center gap-3 mb-2">
                      {t.icon ? <img src={t.icon} alt="" className="h-6 w-6 rounded" /> : <span className="text-lg">üß∞</span>}
                      <span className="truncate font-semibold">{t.name}</span>
                    </div>
                    <p className="text-sm text-slate-500 line-clamp-3">{t.description || '‚Äî'}</p>
                    <div className="mt-3 flex flex-wrap gap-2">
                      {(t.tags || []).slice(0, 4).map((x, i) => (
                        <Badge key={i} variant="secondary" className="capitalize">{x}</Badge>
                      ))}
                    </div>
                    <div className="mt-4">
                      <Button size="sm" variant="outline">{language === 'bg' ? '–û—Ç–≤–æ—Ä–∏' : 'Open'}</Button>
                    </div>
                  </CardContent>
                </Card>
              </a>
            ))}
          </div>
        )}
      </main>
    );
  }
