    resultsFor: 'Results for',
    toolsFound: 'tools',
    savedCount: 'saved',

    // Prompt suggestion
    assistantSuggestion: 'AI Assistant Suggestion',
    copyPasteReady: 'Copy & Paste Ready',
    copy: 'Copy',
