/* ---------- Helpers ---------- */
function groupsFromPlanFallback(plan: PlanItem[]): Group[] {
  const byTitle = new Map<string, Map<string, Tool>>();
  for (const p of plan) {
    const title = p.task;
    const bucket = byTitle.get(title) ?? new Map<string, Tool>();
    for (const t of p.tools) bucket.set(t.name, t);
    byTitle.set(title, bucket);
  }
  return Array.from(byTitle.entries()).map(([title, bucket]) => ({
    title,
    tools: Array.from(bucket.values()),
  }));
}

// Enhanced suggested prompt builder with language support
function buildStepPrompt(goal: string, stepTitle: string, tools: Tool[], language: Language) {
  if (language === 'bg') {
    const goalPart = goal ? `–ú–æ—è—Ç–∞ –≥–ª–∞–≤–Ω–∞ —Ü–µ–ª –µ: "${goal}". ` : '';
    const contextPart = tools.length > 0 
      ? `–¢–∞–∑–∏ —Å—Ç—ä–ø–∫–∞ –≤–∫–ª—é—á–≤–∞ ${tools.length} –ø—Ä–µ–ø–æ—Ä—ä—á–∞–Ω–∏ –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–∞/–ø–æ–¥—Ö–æ–¥–∞. ` 
      : '';

    return (
      `–¢–∏ —Å–∏ –µ–∫—Å–ø–µ—Ä—Ç–µ–Ω –∞—Å–∏—Å—Ç–µ–Ω—Ç, –∫–æ–π—Ç–æ –º–∏ –ø–æ–º–∞–≥–∞ –¥–∞ –ø–æ—Å—Ç–∏–≥–∞–º —Ü–µ–ª–∏—Ç–µ —Å–∏. ` +
      `${goalPart}` +
      `–ù—É–∂–¥–∞—è —Å–µ –æ—Ç –ø–æ–º–æ—â —Å –∫–æ–Ω–∫—Ä–µ—Ç–Ω–∞—Ç–∞ —Å—Ç—ä–ø–∫–∞: "${stepTitle}". ` +
      `${contextPart}` +
      `–ú–æ–ª—è, –ø—Ä–µ–¥–æ—Å—Ç–∞–≤–∏: ` +
      `1) –Ø—Å–µ–Ω –ø–ª–∞–Ω –∑–∞ –¥–µ–π—Å—Ç–≤–∏–µ —Å—Ç—ä–ø–∫–∞ –ø–æ —Å—Ç—ä–ø–∫–∞ –∑–∞ —Ç–∞–∑–∏ –∑–∞–¥–∞—á–∞, ` +
      `2) –ù–∞–π-–¥–æ–±—Ä–∏ –ø—Ä–∞–∫—Ç–∏–∫–∏ –∏ –ø—Ä–æ—Ñ–µ—Å–∏–æ–Ω–∞–ª–Ω–∏ —Å—ä–≤–µ—Ç–∏, ` +
      `3) –ß–µ—Å—Ç–∏ –≥—Ä–µ—à–∫–∏, –∫–æ–∏—Ç–æ –¥–∞ –∏–∑–±—è–≥–≤–∞–º –∏ –∫–∞–∫ –¥–∞ –≥–∏ –ø—Ä–µ–¥–æ—Ç–≤—Ä–∞—Ç—è, ` +
      `4) –ö–∞–∫–≤–∏ –≤—Ö–æ–¥–Ω–∏ –¥–∞–Ω–Ω–∏ —â–µ –º–∏ —Ç—Ä—è–±–≤–∞—Ç –∏ –∫–∞–∫–≤–∏ —Ä–µ–∑—É–ª—Ç–∞—Ç–∏ –¥–∞ –æ—á–∞–∫–≤–∞–º, ` +
      `5) –ö—Ä–∏—Ç–µ—Ä–∏–∏ –∑–∞ –∫–∞—á–µ—Å—Ç–≤–æ –∑–∞ –æ—Ü–µ–Ω–∫–∞ –Ω–∞ —É—Å–ø–µ—Ö–∞. ` +
      `–°–ª–µ–¥ —Ç–æ–≤–∞ –∑–∞–¥–∞–π 2-3 –∫–æ–Ω–∫—Ä–µ—Ç–Ω–∏ –≤—ä–ø—Ä–æ—Å–∞, –∑–∞ –¥–∞ —Ä–∞–∑–±–µ—Ä–µ—à –ø–æ-–¥–æ–±—Ä–µ –º–æ—è –∫–æ–Ω—Ç–µ–∫—Å—Ç, –Ω–∏–≤–æ –Ω–∞ –æ–ø–∏—Ç –∏ –∏–∑–∏—Å–∫–≤–∞–Ω–∏—è.`
    );
  } else {
    const goalPart = goal ? `My main goal is: "${goal}". ` : '';
    const contextPart = tools.length > 0 
      ? `This step involves ${tools.length} recommended tools/approaches. ` 
      : '';

    return (
      `You are an expert assistant helping me achieve my goals. ` +
      `${goalPart}` +
      `I need help with the specific step: "${stepTitle}". ` +
      `${contextPart}` +
      `Please provide: ` +
      `1) A clear step-by-step action plan for this task, ` +
      `2) Best practices and professional tips, ` +
      `3) Common mistakes to avoid and how to prevent them, ` +
      `4) What inputs I'll need and what outputs to expect, ` +
      `5) Quality criteria to evaluate success. ` +
      `Then ask 2-3 specific questions to better understand my context, experience level, and requirements.`
    );
  }
}

// read a cookie by name (for XSRF)
function getCookie(name: string) {
  const parts = document.cookie ? document.cookie.split('; ') : [];
  for (const part of parts) {
    const eq = part.indexOf('=');
    const key = decodeURIComponent(eq > -1 ? part.slice(0, eq) : part);
    if (key === name) {
      const val = eq > -1 ? part.slice(eq + 1) : '';
      return decodeURIComponent(val);
    }
  }
  return null;
}

/* ---------- Simple hash router ---------- */
type View = 'home' | 'favorites';
function getViewFromHash(): View {
  return window.location.hash === '#/favorites' ? 'favorites' : 'home';
}
function goHome() {
  if (window.location.hash !== '' && window.location.hash !== '#/') {
    window.location.hash = '#/';
  } else {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  }
}
function goFavorites() {
  if (window.location.hash !== '#/favorites') window.location.hash = '#/favorites';
}

