      if (savedLang && (savedLang === 'en' || savedLang === 'bg')) {
        setLanguage(savedLang);
      }
    } catch {}
  }, []);

  // Save language preference (stable function to prevent re-renders)
  const saveLanguageTimeoutRef = useRef<NodeJS.Timeout | null>(null);
  const saveLanguage = useCallback((lang: Language) => {
    if (saveLanguageTimeoutRef.current) {
      clearTimeout(saveLanguageTimeoutRef.current);
    }

    saveLanguageTimeoutRef.current = setTimeout(() => {
      setLanguage(prevLang => {
        if (prevLang === lang) return prevLang;

        try {
          localStorage.setItem(LANGUAGE_KEY, lang);
        } catch {}
        return lang;
      });
    }, 100);
  }, []);

