              <label className="inline-flex items-center gap-2 text-slate-700 bg-white/80 border border-slate-200/50 rounded-2xl px-4 h-12 whitespace-nowrap">
                <input 
                  type="checkbox" 
                  checked={showFavsOnly} 
                  onChange={(e) => setShowFavsOnly(e.target.checked)} 
                  className="accent-blue-600 w-4 h-4" 
                />
                <span className="text-sm font-medium">{t('favoritesOnly')}</span>
                <span className="text-xs text-slate-400">({favCount})</span>
              </label>
            </div>
          </section>
        )}

        {/* Visual Plan */}
        {filteredGroups.length > 0 && (
          <section className="mb-16">
            <div className="flex items-center gap-3 mb-8">
              <div className="w-10 h-10 bg-gradient-to-br from-purple-500 to-indigo-600 rounded-2xl flex items-center justify-center shadow-lg shadow-purple-500/25">
                <Wand2 className="w-5 h-5 text-white" />
              </div>
              <div>
                <h2 className="text-2xl text-slate-800 font-bold">{t('journeyTitle')}</h2>
                <p className="text-slate-600 text-sm">{t('journeySubtitle')}</p>
              </div>
            </div>
            <PlanMindMap
              goal={lastGoal || chatInput}
              steps={filteredGroups}
              onOpenTool={handleOpenTool}
              t={t}
              language={language}
            />
          </section>
        )}

        {/* Results */}
        <section ref={resultsRef}>
          {filteredGroups.length > 0 && (
            <>
              <div className="flex items-center justify-between mb-10">
                <div className="flex items-center gap-3">
                  <div className="w-10 h-10 bg-gradient-to-br from-emerald-500 to-green-600 rounded-2xl flex items-center justify-center shadow-lg shadow-emerald-500/25">
                    <Sparkles className="w-5 h-5 text-white" />
                  </div>
                  <div>
                    <h2 className="text-2xl text-slate-800 font-bold">
                      {searchQuery ? `${t('resultsFor')} "${searchQuery}"` : t('resultsTitle')}
                    </h2>
                    <p className="text-slate-600 text-sm">{t('resultsSubtitle')}</p>
                  </div>
                </div>
                <span className="text-slate-500 font-medium bg-slate-100 px-4 py-2 rounded-full text-sm">
                  {uniqueToolCount} {t('toolsFound')} ¬∑ {favCount} {t('savedCount')}
                </span>
              </div>

              <div className="space-y-16">
                {filteredGroups.map((group, index) => {
                  const promptText = buildStepPrompt(lastGoal || chatInput, group.title, group.tools, language);
                  return (
                    <section key={group.title} className="space-y-8">
                      {/* Step Header with Enhanced Prompt Box */}
                      <div className="space-y-6">
                        <div className="flex items-center gap-4">
                          <div className={`w-12 h-12 rounded-2xl flex items-center justify-center font-bold text-white shadow-lg ${
                            index % 3 === 0 ? 'bg-gradient-to-br from-blue-500 to-indigo-600 shadow-blue-500/25' :
                            index % 3 === 1 ? 'bg-gradient-to-br from-purple-500 to-pink-600 shadow-purple-500/25' :
                            'bg-gradient-to-br from-emerald-500 to-green-600 shadow-emerald-500/25'
                          }`}>
                            {index + 1}
                          </div>
                          <div className="flex-1">
                            <h3 className="text-xl font-semibold text-slate-800">{group.title}</h3>
                            <p className="text-slate-600 text-sm">{group.tools.length} {t('recommendedTools')}</p>
                          </div>
                        </div>

                        {/* Enhanced Suggestion Prompt Box */}
                        <div className="bg-gradient-to-r from-amber-50 to-orange-50 border border-amber-200/60 rounded-2xl p-6">
                          <div className="flex items-start gap-4">
                            <div className="w-10 h-10 bg-gradient-to-br from-amber-400 to-orange-500 rounded-xl flex items-center justify-center shadow-lg shadow-amber-500/25 flex-shrink-0">
                              <Lightbulb className="w-5 h-5 text-white" />
                            </div>
                            <div className="flex-1">
                              <div className="flex items-center gap-2 mb-3">
                                <h4 className="font-semibold text-amber-800">{t('assistantSuggestion')}</h4>
                                <Badge variant="secondary" className="text-xs bg-amber-100 text-amber-700 border-amber-200">
                                  {t('copyPasteReady')}
                                </Badge>
                              </div>
                              <PromptBox text={promptText} />
                              <p className="text-xs text-amber-700 mt-3">
                                {t('promptHint')}
                              </p>
                            </div>
                          </div>
                        </div>
                      </div>

                      {/* Tools Grid */}
                      <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6">
                        {group.tools.map((tool) => (
                          <ToolCard 
                            key={`${group.title}-${tool.name}`} 
                            tool={tool} 
                            isFav={favs.has(tool.name)} 
                            onToggleFav={() => toggleFav(tool)} 
                          />
                        ))}
                      </div>
                    </section>
                  );
                })}
              </div>

              {/* Execution Summary (Action Plan) */}
              <div className="mt-16">
                <div className="flex items-center gap-3 mb-4">
                  <div className="w-10 h-10 bg-gradient-to-br from-indigo-500 to-blue-600 rounded-2xl flex items-center justify-center shadow-lg shadow-indigo-500/25">
                    <Sparkles className="w-5 h-5 text-white" />
                  </div>
                  <div>
                    <h3 className="text-2xl font-bold text-slate-800">{t('executionSummary')}</h3>
                    <p className="text-slate-600 text-sm">{t('executionSummarySubtitle')}</p>
                  </div>
                </div>

                <div className="grid md:grid-cols-2 gap-4">
                  {filteredGroups.map((g, i) => {
                    const primary = g.tools[0];
                    const rest = g.tools.slice(1);
                    const hasMany = g.tools.length > 1;
                    return (
                      <div key={`summary-${g.title}-${i}`} className="bg-white/90 border border-slate-200/60 rounded-2xl p-4">
                        <div className="flex items-start justify-between gap-3">
                          <div>
                            <div className="text-xs text-slate-400">{t('forStep')}</div>
                            <div className="font-semibold text-slate-800">{g.title}</div>
                          </div>
                          <div className="text-xs px-2 py-1 bg-slate-100 rounded-full text-slate-600">{g.tools.length} {t('recommendedTools')}</div>
                        </div>

                        <div className="mt-3 space-y-2">
                          {primary && (
                            <div className="flex items-center justify-between bg-emerald-50 border border-emerald-200 rounded-xl p-3">
                              <div className="font-medium text-emerald-800">
                                {language === 'bg' ? '–û—Å–Ω–æ–≤–µ–Ω –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç' : 'Primary tool'}: {primary.name}
                              </div>
                              {primary.link ? (
                                <a
                                  href={primary.link}
                                  target="_blank"
                                  rel="noreferrer noopener"
                                  className="text-xs font-medium px-3 py-1 rounded-lg bg-emerald-600 text-white hover:bg-emerald-700"
                                >
                                  {t('openTool')}
                                </a>
                              ) : (
                                <span className="text-xs text-slate-400">{t('noLink')}</span>
                              )}
                            </div>
                          )}

                          {hasMany && (
                            <div className="bg-slate-50 border border-slate-200 rounded-xl p-3">
                              <div className="text-xs text-slate-500 mb-2">
                                {language === 'bg' ? '–ê–ª—Ç–µ—Ä–Ω–∞—Ç–∏–≤–∏ / –ø–æ–º–æ—â–Ω–∏ –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–∏' : 'Alternatives / helpers'}
                              </div>
                              <div className="flex flex-wrap gap-2">
                                {rest.map((tool) => (
                                  <a
                                    key={`${g.title}-summary-${tool.name}`}
                                    href={tool.link || '#'}
                                    onClick={(e) => { if (!tool.link) e.preventDefault(); }}
                                    target={tool.link ? '_blank' : undefined}
                                    rel={tool.link ? 'noreferrer noopener' : undefined}
                                    className={`text-xs px-2 py-1 rounded-lg border ${
                                      tool.link
                                        ? 'bg-white hover:bg-slate-50 border-slate-200 text-slate-700'
                                        : 'bg-slate-100 border-slate-200 text-slate-400 cursor-not-allowed'
                                    }`}
                                  >
                                    {tool.name}
                                  </a>
                                ))}
                              </div>
                            </div>
                          )}
                        </div>
                      </div>
                    );
                  })}
                </div>
              </div>
            </>
          )}

          {filteredGroups.length === 0 && !loading && groups.length === 0 && (
            <div className="text-center py-20">
              <div className="w-20 h-20 bg-slate-100 rounded-3xl flex items-center justify-center mx-auto mb-6">
                <Sparkles className="w-10 h-10 text-slate-400" />
              </div>
              <h3 className="text-slate-800 mb-3 font-bold text-xl">{t('readyTitle')}</h3>
              <p className="text-slate-600">{t('readySubtitle')}</p>
            </div>
          )}

          {filteredGroups.length === 0 && !loading && groups.length > 0 && (
            <div className="text-center py-16">
              <div className="w-20 h-20 bg-slate-100 rounded-3xl flex items-center justify-center mx-auto mb-6">
                <Search className="w-10 h-10 text-slate-400" />
              </div>
              <h3 className="text-slate-800 mb-3 font-bold text-xl">{t('noToolsTitle')}</h3>
              <p className="text-slate-600">{t('noToolsSubtitle')}</p>
            </div>
          )}
        </section>
      </main>
    );
  }

  // Favorites View Component
  function FavoritesView() {
    return (
      <main className="max-w-6xl mx-auto px-4 py-8 pb-24 md:pb-12">
        <div className="flex items-center justify-between mb-8">
          <div className="flex items-center gap-3">
            <button onClick={goHome} className="inline-flex items-center gap-2 text-slate-600 hover:text-blue-600 transition-colors p-2 -ml-2 rounded-xl">
              <ArrowLeft className="w-4 h-4" />
              <span className="font-medium">{t('back')}</span>
            </button>
            <div className="w-px h-6 bg-slate-300" />
            <h1 className="text-2xl text-slate-800 font-bold">{t('myFavorites')}</h1>
          </div>
          <span className="text-slate-500 font-medium bg-slate-100 px-3 py-1 rounded-full text-sm">
            {favCount} {t('savedCount')}
          </span>
        </div>

        {favCount === 0 ? (
          <div className="text-center py-20">
            <div className="w-20 h-20 bg-slate-100 rounded-3xl flex items-center justify-center mx-auto mb-6">
              <Star className="w-10 h-10 text-slate-400" />
            </div>
            <h3 className="text-slate-800 mb-3 font-bold text-xl">{t('noFavoritesTitle')}</h3>
            <p className="text-slate-600 mb-8">{t('noFavoritesSubtitle')}</p>
            <Button onClick={goHome} className="bg-gradient-to-r from-blue-600 to-indigo-600">
              {t('discoverTools')}
            </Button>
          </div>
        ) : (
          <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {Array.from(favs.values()).map((tool) => (
              <ToolCard 
                key={`fav-${tool.name}`} 
                tool={tool} 
                isFav={true} 
                onToggleFav={() => toggleFav(tool)} 
              />
            ))}
          </div>
        )}
      </main>
    );
  }

  // Enhanced Prompt Box Component
  function PromptBox({ text }: { text: string }) {
    const [copied, setCopied] = useState(false);

    const copy = async () => {
      try {
        await navigator.clipboard.writeText(text);
        setCopied(true);
        setTimeout(() => setCopied(false), 2000);
      } catch {
        setCopied(false);
        alert(t('copyFailed'));
      }
    };

    return (
      <div className="flex items-start gap-3">
        <div className="flex-1 bg-white/80 border border-amber-200 rounded-xl p-4">
          <p className="text-sm text-slate-700 leading-relaxed">{text}</p>
        </div>
        <Button 
          variant="outline" 
          onClick={copy} 
          className={`h-auto px-4 py-3 rounded-xl border-amber-200 hover:bg-amber-50 transition-all ${
            copied ? 'bg-green-50 border-green-200 text-green-700' : 'text-amber-700'
          }`}
        >
          {copied ? <Check className="w-4 h-4 mr-1" /> : <Copy className="w-4 h-4 mr-1" />}
          <span className="text-sm font-medium">{copied ? t('copied') : t('copy')}</span>
        </Button>
      </div>
    );
  }

  // Tool Card Component
  function ToolCard({ tool, isFav, onToggleFav }: { tool: Tool | FavTool; isFav: boolean; onToggleFav: () => void }) {
    return (
      <Card className="border-slate-200/50 hover:shadow-xl transition-all duration-300 hover:-translate-y-1 bg-white/90 backdrop-blur-sm group">
        <CardContent className="p-6">
          <div className="flex items-start justify-between mb-4">
            <div className="w-14 h-14 rounded-2xl overflow-hidden bg-gradient-to-br from-blue-400 to-indigo-500 flex items-center justify-center shadow-lg group-hover:scale-105 transition-transform duration-300">
              {'icon' in tool && (tool as any).icon ? (
                <img
                  src={(tool as any).icon}
                  alt={tool.name}
                  className="w-8 h-8 object-contain"
                  loading="lazy"
                  referrerPolicy="no-referrer"
                  onError={(e) => {
                    const img = e.currentTarget as HTMLImageElement;
                    img.onerror = null;
                    img.style.display = 'none';
                  }}
                />
              ) : (
                <Sparkles className="w-7 h-7 text-white" />
              )}
            </div>

            <button
              onClick={onToggleFav}
              className={`flex items-center gap-1 px-3 py-1.5 rounded-xl border transition-all ${
                isFav 
                  ? 'bg-yellow-100 border-yellow-200 text-yellow-700 hover:bg-yellow-200' 
                  : 'bg-white border-slate-200 text-slate-500 hover:bg-slate-50 hover:border-slate-300'
              }`}
              title={isFav ? t('removeFromFavorites') : t('addToFavorites')}
            >
              <Star className={`w-4 h-4 ${isFav ? 'fill-current' : ''}`} />
              <span className="text-sm font-medium hidden sm:inline">
                {isFav ? t('saved') : t('save')}
              </span>
            </button>
          </div>

          <h3 className="text-slate-800 mb-3 font-bold text-lg leading-tight">{tool.name}</h3>

          <div className="flex gap-2 mb-5">
            <Badge variant="secondary" className="text-xs bg-blue-100 text-blue-700 font-medium px-2 py-1">
              {t('aiTool')}
            </Badge>
            <Badge variant="secondary" className="text-xs font-medium px-2 py-1 bg-emerald-100 text-emerald-700">
              {t('free')}
            </Badge>
          </div>

          {'link' in tool && (tool as any).link ? (
            <a
              href={(tool as any).link}
              target="_blank"
              rel="noreferrer noopener"
              className="block w-full text-center bg-gradient-to-r from-red-500 to-pink-500 hover:from-red-600 hover:to-pink-600 text-white rounded-2xl h-11 leading-[44px] font-medium shadow-lg shadow-red-500/25 transition-all duration-200"
            >
              {t('openTool')}
            </a>
          ) : (
            <Button disabled className="w-full rounded-2xl h-11 bg-slate-100 text-slate-400">
              {t('noLink')}
            </Button>
          )}
        </CardContent>
      </Card>
    );
  }
}
