// frontend/src/App.tsx
import React, { useEffect, useRef, useState } from 'react';
import { Search, Star, MessageCircle, Zap, Sparkles, History, X, ArrowLeft, Copy, Check } from 'lucide-react';

import { Button } from './components/ui/button';
import { Input } from './components/ui/input';
import { Badge } from './components/ui/badge';
import { Card, CardContent } from './components/ui/card';

import { openOAuthPopup, fetchMe, refreshSession, logout } from './auth';
import PlanMindMap from './components/ui/PlanMindMap';

/* ---------- API base ---------- */
const API_BASE =
  (import.meta as any).env?.VITE_API_BASE ||
  (window as any).__API_BASE__ ||
  'http://127.0.0.1:8000';

/* ---------- Types matching backend ---------- */
type Tool = { name: string; link?: string | null; icon?: string | null };
type Group = { title: string; tools: Tool[] };
type PlanItem = { task: string; tools: Tool[] };
type PlanResponse = { goal: string; plan: PlanItem[]; groups?: Group[] };

type CurrentUser = {
  id: number;
  email: string;
  name?: string | null;
  picture?: string | null;
} | null;

/* ---------- Favorites v2 (persist full tool) ---------- */
type FavTool = { name: string; icon?: string | null; link?: string | null };
const FAVS_V2_KEY = 'myai:favs_v2';
const FAVS_V1_KEY = 'myai:favs';

/* ---------- Recent prompts ---------- */
const RECENT_KEY = 'myai:recent_prompts';
const RECENT_MAX = 8;

/* ---------- Helpers ---------- */
function groupsFromPlanFallback(plan: PlanItem[]): Group[] {
  const byTitle = new Map<string, Map<string, Tool>>();
  for (const p of plan) {
    const title = p.task;
    const bucket = byTitle.get(title) ?? new Map<string, Tool>();
    for (const t of p.tools) bucket.set(t.name, t);
    byTitle.set(title, bucket);
  }
  return Array.from(byTitle.entries()).map(([title, bucket]) => ({
    title,
    tools: Array.from(bucket.values()),
  }));
}

// Suggested prompt builder for each step
function buildStepPrompt(goal: string, stepTitle: string, tools: Tool[]) {
  const toolNames = tools.map((t) => t.name).slice(0, 3);
  const toolsPart = toolNames.length ? `Използвай/имай предвид инструментите: ${toolNames.join(', ')}.` : '';
  const g = (goal || '').trim();
  return (
    `Ти си експерт и ми помагаш по целта: "${g || 'моята задача'}". ` +
    `Фокусирай се върху стъпката: "${stepTitle}". ` +
    `${toolsPart} ` +
    `Опиши конкретни действия и примери. Дай кратък, подреден план (списък) и ` +
    `посочи входни данни, които трябва да уточня. В края задай до 3 уточняващи въпроса.`
  );
}

// read a cookie by name (for XSRF)
function getCookie(name: string) {
  const parts = document.cookie ? document.cookie.split('; ') : [];
  for (const part of parts) {
    const eq = part.indexOf('=');
    const key = decodeURIComponent(eq > -1 ? part.slice(0, eq) : part);
    if (key === name) {
      const val = eq > -1 ? part.slice(eq + 1) : '';
      return decodeURIComponent(val);
    }
  }
  return null;
}

/* ---------- Simple hash router ---------- */
type View = 'home' | 'favorites';
function getViewFromHash(): View {
  return window.location.hash === '#/favorites' ? 'favorites' : 'home';
}
function goHome() {
  if (window.location.hash !== '' && window.location.hash !== '#/') {
    window.location.hash = '#/';
  } else {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  }
}
function goFavorites() {
  if (window.location.hash !== '#/favorites') window.location.hash = '#/favorites';
}

export default function App() {
  const [view, setView] = useState<View>(getViewFromHash());

  const [searchQuery, setSearchQuery] = useState('');
  const [chatInput, setChatInput] = useState('');
  const [groups, setGroups] = useState<Group[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [currentUser, setCurrentUser] = useState<CurrentUser>(null);

  // >>> store last goal for visual plan + prompts
  const [lastGoal, setLastGoal] = useState<string>('');

  // dropdowns
  const [signinOpen, setSigninOpen] = useState(false);
  const signinRef = useRef<HTMLDivElement>(null);
  const [profileOpen, setProfileOpen] = useState(false);
  const profileRef = useRef<HTMLDivElement>(null);

  // Email modal
  const [showEmailModal, setShowEmailModal] = useState(false);
  const [authMode, setAuthMode] = useState<'signup' | 'signin'>('signup');
  const [authEmail, setAuthEmail] = useState('');
  const [authPassword, setAuthPassword] = useState('');
  const [authName, setAuthName] = useState('');
  const [authSubmitting, setAuthSubmitting] = useState(false);
  const [authError, setAuthError] = useState<string | null>(null);

  // Profile modal
  const [showProfileModal, setShowProfileModal] = useState(false);
  const [profName, setProfName] = useState('');
  const [profPic, setProfPic] = useState('');
  const [profSaving, setProfSaving] = useState(false);

  // Favorites v2 (map by name)
  const [favs, setFavs] = useState<Map<string, FavTool>>(new Map());
  const [showFavsOnly, setShowFavsOnly] = useState(false);

  // Recent prompts
  const [recents, setRecents] = useState<string[]>([]);

  // Results anchor
  const resultsRef = useRef<HTMLDivElement>(null);

  // ---- hash router listener ----
  useEffect(() => {
    const onHash = () => setView(getViewFromHash());
    window.addEventListener('hashchange', onHash);
    return () => window.removeEventListener('hashchange', onHash);
  }, []);

  // click outside for dropdowns
  useEffect(() => {
    const onDocClick = (e: MouseEvent) => {
      if (signinRef.current && !signinRef.current.contains(e.target as Node)) setSigninOpen(false);
      if (profileRef.current && !profileRef.current.contains(e.target as Node)) setProfileOpen(false);
    };
    document.addEventListener('click', onDocClick, true);
    return () => document.removeEventListener('click', onDocClick, true);
  }, []);

  // load favorites
  useEffect(() => {
    try {
      const v2raw = localStorage.getItem(FAVS_V2_KEY);
      if (v2raw) {
        const arr: FavTool[] = JSON.parse(v2raw) || [];
        setFavs(new Map(arr.map((t) => [t.name, t])));
      } else {
        const v1raw = localStorage.getItem(FAVS_V1_KEY);
        if (v1raw) {
          const names: string[] = JSON.parse(v1raw) || [];
          const arr: FavTool[] = names.map((name) => ({ name }));
          localStorage.setItem(FAVS_V2_KEY, JSON.stringify(arr));
          localStorage.removeItem(FAVS_V1_KEY);
          setFavs(new Map(arr.map((t) => [t.name, t])));
        }
      }
    } catch {}
  }, []);
  function saveFavs(next: Map<string, FavTool>) {
    setFavs(new Map(next));
    try {
      localStorage.setItem(FAVS_V2_KEY, JSON.stringify(Array.from(next.values())));
    } catch {}
  }
  function toggleFav(tool: Tool | FavTool) {
    const next = new Map(favs);
    if (next.has(tool.name)) next.delete(tool.name);
    else next.set(tool.name, { name: tool.name, icon: (tool as any).icon, link: (tool as any).link });
    saveFavs(next);
  }

  // load recents
  useEffect(() => {
    try {
      const raw = localStorage.getItem(RECENT_KEY);
      if (raw) setRecents(JSON.parse(raw));
    } catch {}
  }, []);
  function pushRecent(prompt: string) {
    const p = prompt.trim();
    if (!p) return;
    const next = [p, ...recents.filter((r) => r !== p)].slice(0, RECENT_MAX);
    setRecents(next);
    try {
      localStorage.setItem(RECENT_KEY, JSON.stringify(next));
    } catch {}
  }
  function removeRecent(prompt: string) {
    const next = recents.filter((r) => r !== prompt);
    setRecents(next);
    try {
      localStorage.setItem(RECENT_KEY, JSON.stringify(next));
    } catch {}
  }
  function clearRecents() {
    setRecents([]);
    try {
      localStorage.removeItem(RECENT_KEY);
    } catch {}
  }

  async function fetchPlan(prompt: string): Promise<PlanResponse> {
    const res = await fetch(`${API_BASE}/plan`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ user_goal: prompt }),
    });
    if (!res.ok) throw new Error('Failed to fetch tools from backend');
    return res.json();
  }

  const handleFindTools = async (fromPrompt?: string) => {
    const prompt = (fromPrompt ?? chatInput).trim();
    if (!prompt) return;
    setLoading(true);
    setError(null);
    try {
      const data = await fetchPlan(prompt);
      const grouped = data.groups && data.groups.length ? data.groups : groupsFromPlanFallback(data.plan);
      setGroups(grouped);
      setLastGoal(data.goal || prompt); // for mind map + prompts
      pushRecent(prompt);
      setTimeout(() => resultsRef.current?.scrollIntoView({ behavior: 'smooth', block: 'start' }), 50);
    } catch (e) {
      console.error(e);
      setError('⚠️ Something went wrong while fetching tools.');
    } finally {
      setLoading(false);
    }
  };

  // ------- Auth (cookie session) -------
  async function handleGoogleSignIn() {
    try {
      const { user } = await openOAuthPopup('google');
      setCurrentUser(user);
      setError(null);
    } catch (e) {
      console.error(e);
      setError('Sign in failed. Check backend/.env and CORS.');
    }
  }

  async function handleSignOut() {
    try {
      await logout();
    } catch (e) {
      console.warn('Logout error:', e);
    }
    setCurrentUser(null);
  }

  async function submitEmailAuth() {
    setAuthSubmitting(true);
    setAuthError(null);
    setError(null);
    try {
      const url = authMode === 'signup' ? `${API_BASE}/auth/register` : `${API_BASE}/auth/login`;
      const res = await fetch(url, {
        method: 'POST',
        credentials: 'include',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          email: authEmail,
          password: authPassword,
          name: authMode === 'signup' ? authName : undefined,
        }),
      });

      if (!res.ok) {
        const t = await res.text();
        throw new Error(t || `HTTP ${res.status}`);
      }

      const data = await res.json();
      setCurrentUser(data.user);
      setShowEmailModal(false);
      setAuthEmail('');
      setAuthPassword('');
      setAuthName('');
    } catch (e: any) {
      console.error(e);
      setAuthError(authMode === 'signup' ? 'Sign up failed.' : 'Sign in failed.');
    } finally {
      setAuthSubmitting(false);
    }
  }

  // Profile modal open
  function openProfile() {
    if (!currentUser) return;
    setProfName(currentUser.name ?? '');
    setProfPic(currentUser.picture ?? '');
    setShowProfileModal(true);
    setProfileOpen(false);
  }

  // Save profile (PATCH /auth/me)
  async function saveProfile() {
    if (!currentUser) return;
    setProfSaving(true);
    setError(null);
    try {
      const xsrf = getCookie('XSRF-TOKEN');
      const res = await fetch(`${API_BASE}/auth/me`, {
        method: 'PATCH',
        credentials: 'include',
        headers: {
          'Content-Type': 'application/json',
          ...(xsrf ? { 'X-XSRF-TOKEN': xsrf } : {}),
        },
        body: JSON.stringify({ name: profName, picture: profPic }),
      });
      if (!res.ok) {
        const t = await res.text();
        throw new Error(t || 'Update failed');
      }
      const user = await res.json();
      setCurrentUser(user);
      setShowProfileModal(false);
    } catch (e) {
      console.error(e);
      setError('Profile update failed.');
    } finally {
      setProfSaving(false);
    }
  }

  // auto-load session
  useEffect(() => {
    let mounted = true;
    (async () => {
      try {
        const u = await fetchMe();
        if (mounted) setCurrentUser(u);
      } catch {
        try {
          await refreshSession();
          const u2 = await fetchMe();
          if (mounted) setCurrentUser(u2);
        } catch {
          if (mounted) setCurrentUser(null);
        }
      }
    })();
    return () => { mounted = false; };
  }, []);

  // филтриране по търсене + favorites (само на home)
  const term = searchQuery.trim().toLowerCase();
  const filteredGroups: Group[] = groups
    .map((g) => ({
      title: g.title,
      tools: g.tools.filter((t) => {
        const match = !term || g.title.toLowerCase().includes(term) || (t.name ?? '').toLowerCase().includes(term);
        const favOk = !showFavsOnly || favs.has(t.name);
        return match && favOk;
      }),
    }))
    .filter((g) => g.tools.length > 0);

  const favCount = favs.size;
  const uniqueToolCount = new Set(filteredGroups.flatMap((g) => g.tools.map((t) => t.name))).size;

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-50">
      {/* Header */}
      <header className="sticky top-0 z-50 bg-white/70 backdrop-blur-xl border-b border-slate-200/50">
        <div className="max-w-6xl mx-auto px-4 py-4 flex items-center justify-between">
          <div className="flex items-center space-x-3 cursor-pointer" onClick={goHome}>
            <div className="w-9 h-9 bg-gradient-to-br from-blue-500 to-indigo-600 rounded-xl flex items-center justify-center shadow-lg shadow-blue-500/25">
              <Sparkles className="w-5 h-5 text-white" />
            </div>
            <span className="text-xl text-slate-800 font-semibold">My AI</span>
          </div>

          <nav className="hidden md:flex items-center space-x-6">
            <button onClick={goHome} className={`font-medium ${view==='home'?'text-blue-600':'text-slate-600 hover:text-blue-600'}`}>
              Discover
            </button>
            <a href="#" className="text-slate-600 hover:text-blue-600 font-medium">Categories</a>
            <a href="#" className="text-slate-600 hover:text-blue-600 font-medium">Learn</a>

            {/* Favorites button -> route */}
            <button
              onClick={goFavorites}
              className={`inline-flex items-center gap-2 font-medium ${view==='favorites'?'text-blue-600':'text-slate-700 hover:text-blue-600'}`}
              title="My Favorites"
            >
              <Star className={`w-4 h-4 ${favCount ? 'fill-yellow-400 text-yellow-500' : ''}`} />
              Favorites ({favCount})
            </button>

            {/* Right side: auth */}
            {currentUser ? (
              <div className="relative" ref={profileRef}>
                <button onClick={() => setProfileOpen((v) => !v)} className="flex items-center space-x-2" title={currentUser.email}>
                  {currentUser.picture ? (
                    <img src={currentUser.picture} alt="avatar" className="w-8 h-8 rounded-full border border-slate-200" referrerPolicy="no-referrer" />
                  ) : (<div className="w-8 h-8 rounded-full bg-blue-200" />)}
                  <span className="text-slate-700 font-medium max-w-[140px] truncate">{currentUser.name || currentUser.email}</span>
                </button>
                {profileOpen && (
                  <div className="absolute right-0 mt-2 w-56 bg-white rounded-2xl shadow-xl border border-slate-200/60 p-1 z-50">
                    <button onClick={openProfile} className="w-full text-left px-3 py-2 rounded-xl hover:bg-slate-50 transition">Profile</button>
                    <div className="my-1 border-t border-slate-100" />
                    <button onClick={handleSignOut} className="w-full text-left px-3 py-2 rounded-xl hover:bg-slate-50 transition text-red-600">Sign Out</button>
                  </div>
                )}
              </div>
            ) : (
              <div className="relative" ref={signinRef}>
                <Button variant="outline" size="sm" onClick={() => setSigninOpen((v) => !v)} className="border-blue-300 text-blue-600 hover:bg-blue-50 font-medium">
                  Sign In
                </Button>
                {signinOpen && (
                  <div className="absolute right-0 mt-2 w-64 bg-white rounded-2xl shadow-xl border border-slate-200/60 p-1 z-50">
                    <button
                      onClick={() => { setSigninOpen(false); setAuthMode('signup'); setShowEmailModal(true); }}
                      className="w-full flex items-center px-3 py-2 rounded-xl hover:bg-slate-50 transition"
                    >
                      <div className="w-6 h-6 rounded-md bg-slate-200 mr-3" />
                      <div className="flex flex-col items-start">
                        <span className="text-slate-800 font-medium">Create account</span>
                        <span className="text-slate-400 text-xs">email / password</span>
                      </div>
                    </button>
                    <div className="my-1 border-t border-slate-100" />
                    <button
                      onClick={() => { setSigninOpen(false); handleGoogleSignIn(); }}
                      className="w-full flex items-center px-3 py-2 rounded-xl hover:bg-slate-50 transition"
                    >
                      <img src="https://www.gstatic.com/firebasejs/ui/2.0.0/images/auth/google.svg" alt="Google" className="w-4 h-4 mr-3" />
                      <span className="text-slate-800 font-medium">Sign in with Google</span>
                    </button>
                  </div>
                )}
              </div>
            )}
          </nav>
        </div>
      </header>

      {/* Email signup/signin modal */}
      {showEmailModal && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/30 backdrop-blur-sm">
          <div className="bg-white w-full max-w-md rounded-2xl shadow-2xl p-6">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold text-slate-800">
                {authMode === 'signup' ? 'Create your account' : 'Sign in to your account'}
              </h3>
              <button onClick={() => setShowEmailModal(false)} className="text-slate-500 hover:text-slate-700">✕</button>
            </div>

            <div className="space-y-3">
              {authMode === 'signup' && (
                <input className="w-full border border-slate-200 rounded-xl px-3 py-2" placeholder="Your name (optional)" value={authName} onChange={(e) => setAuthName(e.target.value)} />
              )}
              <input className="w-full border border-slate-200 rounded-xl px-3 py-2" placeholder="Email" type="email" value={authEmail} onChange={(e) => setAuthEmail(e.target.value)} />
              <input className="w-full border border-slate-200 rounded-xl px-3 py-2" placeholder="Password" type="password" value={authPassword} onChange={(e) => setAuthPassword(e.target.value)} />
              {authError && <div className="text-red-600 text-sm mt-1">{authError}</div>}
            </div>

            <div className="flex items-center justify-between mt-5">
              <button onClick={() => setAuthMode(authMode === 'signup' ? 'signin' : 'signup')} className="text-sm text-blue-600 hover:underline">
                {authMode === 'signup' ? 'Already have an account? Sign in' : "Don't have an account? Create one"}
              </button>
              <div className="flex gap-2">
                <Button variant="outline" onClick={() => setShowEmailModal(false)}>Cancel</Button>
                <Button onClick={submitEmailAuth} disabled={authSubmitting} className="bg-gradient-to-r from-blue-600 to-indigo-600">
                  {authMode === 'signup' ? 'Create account' : 'Sign in'}
                </Button>
              </div>
            </div>

            <div className="mt-4">
              <div className="text-center text-xs text-slate-400 mb-2">or</div>
              <Button onClick={() => { setShowEmailModal(false); handleGoogleSignIn(); }} className="w-full bg-white border border-slate-200 text-slate-800 hover:bg-slate-50">
                <img src="https://www.gstatic.com/firebasejs/ui/2.0.0/images/auth/google.svg" alt="Google" className="w-4 h-4 mr-2" />
                Continue with Google
              </Button>
            </div>
          </div>
        </div>
      )}

      {/* Profile modal */}
      {showProfileModal && currentUser && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/30 backdrop-blur-sm">
          <div className="bg-white w-full max-w-md rounded-2xl shadow-2xl p-6">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold text-slate-800">Your Profile</h3>
              <button onClick={() => setShowProfileModal(false)} className="text-slate-500 hover:text-slate-700">✕</button>
            </div>

            <div className="flex items-center gap-4 mb-4">
              <div className="w-14 h-14 rounded-full overflow-hidden bg-slate-100 border border-slate-200">
                {profPic ? (<img src={profPic} className="w-full h-full object-cover" referrerPolicy="no-referrer" />) : (<div className="w-full h-full" />)}
              </div>
              <div className="flex-1">
                <div className="text-xs text-slate-500 mb-1">Email</div>
                <div className="text-slate-800 font-medium">{currentUser.email}</div>
              </div>
            </div>

            <div className="space-y-3">
              <div>
                <div className="text-xs text-slate-500 mb-1">Name</div>
                <input className="w-full border border-slate-200 rounded-xl px-3 py-2" placeholder="Your name" value={profName} onChange={(e) => setProfName(e.target.value)} />
              </div>
              <div>
                <div className="text-xs text-slate-500 mb-1">Avatar URL</div>
                <input className="w-full border border-slate-200 rounded-xl px-3 py-2" placeholder="https://..." value={profPic} onChange={(e) => setProfPic(e.target.value)} />
              </div>
            </div>

            <div className="flex justify-end gap-2 mt-6">
              <Button variant="outline" onClick={() => setShowProfileModal(false)}>Cancel</Button>
              <Button onClick={saveProfile} disabled={profSaving} className="bg-gradient-to-r from-blue-600 to-indigo-600">Save changes</Button>
            </div>
          </div>
        </div>
      )}

      {/* Main Content */}
      {view === 'home' ? (
        <main className="max-w-6xl mx-auto px-4 py-12 pb-24 md:pb-12">
          {/* Hero */}
          <section className="text-center mb-16">
            <h1 className="text-5xl md:text-6xl text-slate-800 mb-6 font-bold tracking-tight">
              Discover AI tools that <span className="bg-gradient-to-r from-blue-600 to-indigo-600 bg-clip-text text-transparent">actually help</span>
            </h1>
            <p className="text-xl text-slate-600 mb-10 max-w-2xl mx-auto leading-relaxed">
              From writing essays to creating art, find the perfect AI tools for your goals.
            </p>

            {/* Input */}
            <div className="max-w-2xl mx-auto mb-6">
              <div className="relative bg-white/80 backdrop-blur-sm rounded-3xl p-8 border border-slate-200/50 shadow-2xl shadow-blue-500/10">
                <div className="flex items-center space-x-4 mb-6">
                  <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-indigo-600 rounded-2xl flex items-center justify-center shadow-lg shadow-blue-500/25">
                    <MessageCircle className="w-6 h-6 text-white" />
                  </div>
                  <span className="text-slate-700 text-lg font-medium">What do you want to achieve with AI?</span>
                </div>
                <div className="flex space-x-3">
                  <Input
                    placeholder="e.g., Write better emails, create a logo, analyze data..."
                    value={chatInput}
                    onChange={(e) => setChatInput(e.target.value)}
                    onKeyDown={(e) => { if (e.key === 'Enter') handleFindTools(); }}
                    className="flex-1 bg-slate-50/80 border-slate-200 text-base h-14 rounded-2xl px-5 font-medium placeholder:text-slate-400"
                  />
                  <Button onClick={() => handleFindTools()} className="bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 px-8 h-14 rounded-2xl shadow-lg shadow-blue-500/25 font-medium">
                    <Zap className="w-5 h-5 mr-2" /> Find Tools
                  </Button>
                </div>
              </div>
            </div>

            {/* Recent prompts */}
            {recents.length > 0 && (
              <div className="max-w-2xl mx-auto mb-10">
                <div className="flex items-center justify-between mb-2 px-1">
                  <div className="flex items-center gap-2 text-slate-600"><History className="w-4 h-4" /><span className="text-sm font-medium">Recent prompts</span></div>
                  <button className="text-xs text-slate-400 hover:text-slate-600" onClick={clearRecents}>Clear</button>
                </div>
                <div className="flex flex-wrap gap-2">
                  {recents.map((p) => (
                    <div key={p} className="group inline-flex items-center max-w-full bg-white border border-slate-200 rounded-xl px-3 py-1.5 shadow-sm">
                      <button className="text-sm text-slate-700 truncate max-w-[220px] hover:text-blue-600" title={p} onClick={() => { setChatInput(p); handleFindTools(p); }}>
                        {p}
                      </button>
                      <button className="ml-2 text-slate-400 hover:text-slate-600" onClick={() => removeRecent(p)} aria-label="remove" title="Remove">
                        <X className="w-3.5 h-3.5" />
                      </button>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {error && <div className="text-red-600 font-medium mb-6">{error}</div>}
            {loading && <div className="text-blue-600 font-medium mb-6">🔄 Finding the best tools for you...</div>}
          </section>

          {/* Filters */}
          <section className="mb-10">
            <div className="flex flex-col sm:flex-row gap-4 mb-8 items-center">
              <div className="relative flex-1 w-full">
                <Search className="absolute left-4 top-1/2 transform -translate-y-1/2 text-slate-400 w-5 h-5" />
                <Input
                  placeholder="Search tools, categories, or goals..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pl-12 bg-white/80 border-slate-200/50 h-12 rounded-2xl backdrop-blur-sm font-medium"
                />
              </div>

              {/* Favorites filter */}
              <label className="inline-flex items-center gap-2 text-slate-700 bg-white/80 border border-slate-200/50 rounded-2xl px-3 h-12">
                <input type="checkbox" checked={showFavsOnly} onChange={(e) => setShowFavsOnly(e.target.checked)} className="accent-blue-600 w-4 h-4" />
                <span className="text-sm font-medium">Show favorites only</span>
                <span className="text-xs text-slate-400">({favCount})</span>
              </label>
            </div>
          </section>

          {/* Visual plan */}
          {filteredGroups.length > 0 && (
            <section className="mb-12">
              <h2 className="text-3xl text-slate-800 font-bold mb-4">Visual plan</h2>
              <PlanMindMap
                goal={lastGoal || chatInput}
                steps={filteredGroups}
                className="mt-2"
                onOpenTool={() => {}}
              />
            </section>
          )}

          {/* Results (GROUPED) */}
          <section ref={resultsRef}>
            <div className="flex items-center justify-between mb-8">
              <h2 className="text-3xl text-slate-800 font-bold">
                {searchQuery ? `Results for "${searchQuery}"` : 'Generated AI Tools'}
              </h2>
              <span className="text-slate-500 font-medium">
                {uniqueToolCount} tools · {favCount} favorites
              </span>
            </div>

            {filteredGroups.length === 0 && !loading && (
              <div className="text-center py-16">
                <div className="w-20 h-20 bg-slate-100 rounded-3xl flex items-center justify-center mx-auto mb-6">
                  <Search className="w-10 h-10 text-slate-400" />
                </div>
                <h3 className="text-slate-800 mb-3 font-bold text-xl">No tools found</h3>
                <p className="text-slate-600">Try adjusting your search or filters.</p>
              </div>
            )}

            <div className="space-y-12">
              {filteredGroups.map((group) => {
                const promptText = buildStepPrompt(lastGoal || chatInput, group.title, group.tools);
                return (
                  <section key={group.title} className="space-y-5">
                    {/* Step header + prompt box */}
                    <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-3">
                      <h3 className="text-xl font-semibold text-slate-800">{group.title}</h3>
                      <PromptBox text={promptText} />
                    </div>

                    {/* Tools grid */}
                    <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6">
                      {group.tools.map((tool) => (
                        <ToolCard key={`${group.title}-${tool.name}`} tool={tool} isFav={favs.has(tool.name)} onToggleFav={() => toggleFav(tool)} />
                      ))}
                    </div>
                  </section>
                );
              })}
            </div>
          </section>
        </main>
      ) : (
        // -------- My Favorites view --------
        <main className="max-w-6xl mx-auto px-4 py-12 pb-24 md:pb-12">
          <div className="flex items-center justify-between mb-8">
            <div className="flex items-center gap-3">
              <button onClick={goHome} className="inline-flex items-center gap-1 text-slate-600 hover:text-blue-600">
                <ArrowLeft className="w-4 h-4" /> Back
              </button>
              <h2 className="text-3xl text-slate-800 font-bold">My Favorites</h2>
            </div>
            <span className="text-slate-500 font-medium">{favCount} saved</span>
          </div>

          {favCount === 0 ? (
            <div className="text-center py-20">
              <div className="w-20 h-20 bg-slate-100 rounded-3xl flex items-center justify-center mx-auto mb-6">
                <Star className="w-10 h-10 text-slate-400" />
              </div>
              <h3 className="text-slate-800 mb-3 font-bold text-xl">No favorites yet</h3>
              <p className="text-slate-600 mb-6">Mark tools with the star to save them here.</p>
              <Button onClick={goHome} className="bg-gradient-to-r from-blue-600 to-indigo-600">Discover tools</Button>
            </div>
          ) : (
            <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6">
              {Array.from(favs.values()).map((tool) => (
                <ToolCard key={`fav-${tool.name}`} tool={tool} isFav={true} onToggleFav={() => toggleFav(tool)} />
              ))}
            </div>
          )}
        </main>
      )}
    </div>
  );
}

/* ---------- Small PromptBox with copy ---------- */
function PromptBox({ text }: { text: string }) {
  const [copied, setCopied] = useState(false);

  const copy = async () => {
    try {
      await navigator.clipboard.writeText(text);
      setCopied(true);
      setTimeout(() => setCopied(false), 1200);
    } catch {
      // fallback: select text for manual copy
      setCopied(false);
      alert('Copy failed. Select and copy manually.');
    }
  };

  return (
    <div className="flex items-center gap-2 w-full md:w-[560px]">
      <Input
        readOnly
        value={text}
        onFocus={(e) => e.currentTarget.select()}
        className="bg-white/80 border-slate-200/60 text-[13px] md:text-sm h-10 rounded-xl"
        aria-label="Suggested prompt"
      />
      <Button variant="outline" onClick={copy} className="h-10 rounded-xl">
        {copied ? <Check className="w-4 h-4 mr-1" /> : <Copy className="w-4 h-4 mr-1" />}
        {copied ? 'Copied' : 'Copy'}
      </Button>
    </div>
  );
}

/* ---------- Reusable tool card ---------- */
function ToolCard({ tool, isFav, onToggleFav }: { tool: Tool | FavTool; isFav: boolean; onToggleFav: () => void }) {
  return (
    <Card className="border-slate-200/50 hover:shadow-xl transition-all duration-300 hover:-translate-y-2 bg-white/80 backdrop-blur-sm group">
      <CardContent className="p-6">
        <div className="flex items-start justify-between mb-5">
          <div className="w-14 h-14 rounded-2xl overflow-hidden bg-gradient-to-br from-blue-400 to-indigo-500 flex items-center justify-center shadow-lg group-hover:scale-110 transition-transform duration-300">
            {'icon' in tool && (tool as any).icon ? (
              <img
                src={(tool as any).icon}
                alt={tool.name}
                className="w-10 h-10 object-contain"
                loading="lazy"
                referrerPolicy="no-referrer"
                onError={(e) => {
                  const img = e.currentTarget as HTMLImageElement;
                  img.onerror = null;
                  img.src = '/icons/default-tool.svg';
                }}
              />
            ) : (
              <Sparkles className="w-7 h-7 text-white" />
            )}
          </div>

          <button
            onClick={onToggleFav}
            className={`flex items-center gap-1 px-3 py-1.5 rounded-xl border transition
              ${isFav ? 'bg-yellow-100 border-yellow-200 text-yellow-700' : 'bg-white border-slate-200 text-slate-500 hover:bg-slate-50'}`}
            title={isFav ? 'Remove from favorites' : 'Add to favorites'}
          >
            <Star className={`w-4 h-4 ${isFav ? 'fill-current' : ''}`} />
            <span className="text-sm font-medium">{isFav ? 'Favorited' : 'Favorite'}</span>
          </button>
        </div>

        <h3 className="text-slate-800 mb-2 font-bold text-lg">{tool.name}</h3>
        <p className="text-slate-600 text-sm mb-5 leading-relaxed">{/* optional */}</p>
        <div className="flex space-x-2 mb-5">
          <Badge variant="secondary" className="text-xs bg-blue-100 text-blue-700 font-medium px-3 py-1">Generated</Badge>
          <Badge variant="secondary" className="text-xs font-medium px-3 py-1 bg-emerald-100 text-emerald-700">Free</Badge>
        </div>

        {'link' in tool && (tool as any).link ? (
          <a
            href={(tool as any).link}
            target="_blank"
            rel="noreferrer noopener"
            className="block w-full text-center bg-gradient-to-r from-red-500 to-pink-500 hover:from-red-600 hover:to-pink-600 text-white rounded-2xl h-11 leading-[44px] font-medium shadow-lg shadow-red-500/25"
          >
            Open Tool
          </a>
        ) : (
          <Button disabled className="w-full rounded-2xl h-11">No Link</Button>
        )}
      </CardContent>
    </Card>
  );
}
