import React, { useState, useMemo } from 'react';
import { ChevronRight, Sparkles, Target, ArrowRight, ArrowDown, Circle, Link as LinkIcon, Users, CheckCircle2, MapPin, Lightbulb, Copy, Check } from 'lucide-react';
import { Button } from './button';
import { Card, CardContent } from './card';
import { Badge } from './badge';

export type Tool = { name: string; link?: string | null; icon?: string | null };
export type Group = { title: string; tools: Tool[] };

// Shared tool type for tools that appear in multiple steps
type SharedTool = {
  tool: Tool;
  stepIndices: number[];
  stepTitles: string[];
};

// Analysis result type
type ToolAnalysis = {
  sharedTools: SharedTool[];
  uniqueToolsByStep: Map<number, Tool[]>;
};

export interface PlanMindMapProps {
  goal: string;
  steps: Group[];
  className?: string;
  onOpenTool: (tool: Tool) => void;
  t?: (key: string) => string;
  language?: 'en' | 'bg';
}

export default function PlanMindMap({ goal, steps, className = '', onOpenTool, t, language = 'en' }: PlanMindMapProps) {
  // Default translations if t function is not provided
  const defaultTranslations = {
    showSharedTools: 'Show',
    hideSharedTools: 'Hide',
    sharedTools: 'Shared Tools',
    toolsUsedAcrossSteps: 'Tools used across multiple steps',
    stepSpecificTools: 'Step-specific tools',
    sharedToolsForStep: 'Shared tools for this step',
    usedInSteps: 'Used in steps',
    usedInXSteps: 'Used in {count} steps',
    uniqueTools: 'unique tools',
    sharedAcrossSteps: 'shared across steps',
    stepsTotal: 'steps total',
    sharedToolsHint: 'Shared tools appear in the top section and are highlighted when viewing individual steps',
    executionSummary: 'Execution Summary',
    executionSummarySubtitle: 'Quick overview of your AI workflow',
    forSteps: 'For steps',
    forStep: 'For step',
    useTheseTool: 'use this tool',
    useTheseTools: 'use these tools',
    toolRecommendation: 'Tool Recommendation',
    assistantSuggestion: 'AI Assistant Suggestion',
    copyPasteReady: 'Copy & Paste Ready',
    copy: 'Copy',
    copied: 'Copied!',
    promptHint: 'ðŸ’¡ Copy this prompt and paste it into any AI assistant (ChatGPT, Claude, etc.) for personalized guidance on this step.',
    promptForStep: 'AI Prompt for Step',
    promptCopied: 'Copied!',
    copyPrompt: 'Copy',
    aiTool: 'AI Tool',
    showSharedTools: 'Show',
    hideSharedTools: 'Hide',
    sharedTools: 'Shared Tools',
    toolsUsedAcrossSteps: 'Tools used across multiple steps',
  };
  
  const translate = (key: string, params?: { [key: string]: string | number }): string => {
    let text = t ? t(key) : (defaultTranslations as any)[key] || key;
    if (params) {
      Object.entries(params).forEach(([paramKey, value]) => {
        text = text.replace(`{${paramKey}}`, String(value));
      });
    }
    return text;
  };
  
  const [activeStep, setActiveStep] = useState<number | null>(null);
  const [hoveredTool, setHoveredTool] = useState<string | null>(null);
  const [showSharedTools, setShowSharedTools] = useState(true);
  const [visiblePrompts, setVisiblePrompts] = useState<Set<number>>(new Set());
  const [copiedPrompts, setCopiedPrompts] = useState<Set<number>>(new Set());

  // Analyze tools to find shared ones
  const toolAnalysis = useMemo((): ToolAnalysis => {
    const toolOccurrences = new Map<string, { tool: Tool; stepIndices: number[]; stepTitles: string[] }>();
    const uniqueToolsByStep = new Map<number, Tool[]>();

    // Collect all tool occurrences
    steps.forEach((step, stepIndex) => {
      step.tools.forEach((tool) => {
        const existing = toolOccurrences.get(tool.name);
        if (existing) {
          existing.stepIndices.push(stepIndex);
          existing.stepTitles.push(step.title);
        } else {
          toolOccurrences.set(tool.name, {
            tool,
            stepIndices: [stepIndex],
            stepTitles: [step.title]
          });
        }
      });
    });

    // Separate shared tools (appear in 2+ steps) from unique tools
    const sharedTools: SharedTool[] = [];
    
    steps.forEach((step, stepIndex) => {
      uniqueToolsByStep.set(stepIndex, []);
    });

    toolOccurrences.forEach((occurrence) => {
      if (occurrence.stepIndices.length >= 2) {
        // This is a shared tool
        sharedTools.push(occurrence);
      } else {
        // This is a unique tool for this step
        const stepIndex = occurrence.stepIndices[0];
        const stepTools = uniqueToolsByStep.get(stepIndex) || [];
        stepTools.push(occurrence.tool);
        uniqueToolsByStep.set(stepIndex, stepTools);
      }
    });

    return { sharedTools, uniqueToolsByStep };
  }, [steps]);

  // Enhanced suggested prompt builder with language support
  function buildStepPrompt(stepTitle: string, tools: Tool[]) {
    if (language === 'bg') {
      const goalPart = goal ? `ÐœÐ¾ÑÑ‚Ð° Ð³Ð»Ð°Ð²Ð½Ð° Ñ†ÐµÐ» Ðµ: "${goal}". ` : '';
      const contextPart = tools.length > 0 
        ? `Ð¢Ð°Ð·Ð¸ ÑÑ‚ÑŠÐ¿ÐºÐ° Ð²ÐºÐ»ÑŽÑ‡Ð²Ð° ${tools.length} Ð¿Ñ€ÐµÐ¿Ð¾Ñ€ÑŠÑ‡Ð°Ð½Ð¸ Ð¸Ð½ÑÑ‚Ñ€ÑƒÐ¼ÐµÐ½Ñ‚Ð°/Ð¿Ð¾Ð´Ñ…Ð¾Ð´Ð°. ` 
        : '';
      
      return (
        `Ð¢Ð¸ ÑÐ¸ ÐµÐºÑÐ¿ÐµÑ€Ñ‚ÐµÐ½ Ð°ÑÐ¸ÑÑ‚ÐµÐ½Ñ‚, ÐºÐ¾Ð¹Ñ‚Ð¾ Ð¼Ð¸ Ð¿Ð¾Ð¼Ð°Ð³Ð° Ð´Ð° Ð¿Ð¾ÑÑ‚Ð¸Ð³Ð°Ð¼ Ñ†ÐµÐ»Ð¸Ñ‚Ðµ ÑÐ¸. ` +
        `${goalPart}` +
        `ÐÑƒÐ¶Ð´Ð°Ñ ÑÐµ Ð¾Ñ‚ Ð¿Ð¾Ð¼Ð¾Ñ‰ Ñ ÐºÐ¾Ð½ÐºÑ€ÐµÑ‚Ð½Ð°Ñ‚Ð° ÑÑ‚ÑŠÐ¿ÐºÐ°: "${stepTitle}". ` +
        `${contextPart}` +
        `ÐœÐ¾Ð»Ñ, Ð¿Ñ€ÐµÐ´Ð¾ÑÑ‚Ð°Ð²Ð¸: ` +
        `1) Ð¯ÑÐµÐ½ Ð¿Ð»Ð°Ð½ Ð·Ð° Ð´ÐµÐ¹ÑÑ‚Ð²Ð¸Ðµ ÑÑ‚ÑŠÐ¿ÐºÐ° Ð¿Ð¾ ÑÑ‚ÑŠÐ¿ÐºÐ° Ð·Ð° Ñ‚Ð°Ð·Ð¸ Ð·Ð°Ð´Ð°Ñ‡Ð°, ` +
        `2) ÐÐ°Ð¹-Ð´Ð¾Ð±Ñ€Ð¸ Ð¿Ñ€Ð°ÐºÑ‚Ð¸ÐºÐ¸ Ð¸ Ð¿Ñ€Ð¾Ñ„ÐµÑÐ¸Ð¾Ð½Ð°Ð»Ð½Ð¸ ÑÑŠÐ²ÐµÑ‚Ð¸, ` +
        `3) Ð§ÐµÑÑ‚Ð¸ Ð³Ñ€ÐµÑˆÐºÐ¸, ÐºÐ¾Ð¸Ñ‚Ð¾ Ð´Ð° Ð¸Ð·Ð±ÑÐ³Ð²Ð°Ð¼ Ð¸ ÐºÐ°Ðº Ð´Ð° Ð³Ð¸ Ð¿Ñ€ÐµÐ´Ð¾Ñ‚Ð²Ñ€Ð°Ñ‚Ñ, ` +
        `4) ÐšÐ°ÐºÐ²Ð¸ Ð²Ñ…Ð¾Ð´Ð½Ð¸ Ð´Ð°Ð½Ð½Ð¸ Ñ‰Ðµ Ð¼Ð¸ Ñ‚Ñ€ÑÐ±Ð²Ð°Ñ‚ Ð¸ ÐºÐ°ÐºÐ²Ð¸ Ñ€ÐµÐ·ÑƒÐ»Ñ‚Ð°Ñ‚Ð¸ Ð´Ð° Ð¾Ñ‡Ð°ÐºÐ²Ð°Ð¼, ` +
        `5) ÐšÑ€Ð¸Ñ‚ÐµÑ€Ð¸Ð¸ Ð·Ð° ÐºÐ°Ñ‡ÐµÑÑ‚Ð²Ð¾ Ð·Ð° Ð¾Ñ†ÐµÐ½ÐºÐ° Ð½Ð° ÑƒÑÐ¿ÐµÑ…Ð°. ` +
        `Ð¡Ð»ÐµÐ´ Ñ‚Ð¾Ð²Ð° Ð·Ð°Ð´Ð°Ð¹ 2-3 ÐºÐ¾Ð½ÐºÑ€ÐµÑ‚Ð½Ð¸ Ð²ÑŠÐ¿Ñ€Ð¾ÑÐ°, Ð·Ð° Ð´Ð° Ñ€Ð°Ð·Ð±ÐµÑ€ÐµÑˆ Ð¿Ð¾-Ð´Ð¾Ð±Ñ€Ðµ Ð¼Ð¾Ñ ÐºÐ¾Ð½Ñ‚ÐµÐºÑÑ‚, Ð½Ð¸Ð²Ð¾ Ð½Ð° Ð¾Ð¿Ð¸Ñ‚ Ð¸ Ð¸Ð·Ð¸ÑÐºÐ²Ð°Ð½Ð¸Ñ.`
      );
    } else {
      const goalPart = goal ? `My main goal is: "${goal}". ` : '';
      const contextPart = tools.length > 0 
        ? `This step involves ${tools.length} recommended tools/approaches. ` 
        : '';
      
      return (
        `You are an expert assistant helping me achieve my goals. ` +
        `${goalPart}` +
        `I need help with the specific step: "${stepTitle}". ` +
        `${contextPart}` +
        `Please provide: ` +
        `1) A clear step-by-step action plan for this task, ` +
        `2) Best practices and professional tips, ` +
        `3) Common mistakes to avoid and how to prevent them, ` +
        `4) What inputs I'll need and what outputs to expect, ` +
        `5) Quality criteria to evaluate success. ` +
        `Then ask 2-3 specific questions to better understand my context, experience level, and requirements.`
      );
    }
  }

  const togglePrompt = (stepIndex: number) => {
    const newVisible = new Set(visiblePrompts);
    if (newVisible.has(stepIndex)) {
      newVisible.delete(stepIndex);
    } else {
      newVisible.add(stepIndex);
    }
    setVisiblePrompts(newVisible);
  };

  const copyPrompt = async (stepIndex: number, prompt: string) => {
    try {
      await navigator.clipboard.writeText(prompt);
      const newCopied = new Set(copiedPrompts);
      newCopied.add(stepIndex);
      setCopiedPrompts(newCopied);
      setTimeout(() => {
        setCopiedPrompts(prev => {
          const updated = new Set(prev);
          updated.delete(stepIndex);
          return updated;
        });
      }, 2000);
    } catch {
      // Fallback for copy failure
      const newCopied = new Set(copiedPrompts);
      newCopied.add(stepIndex);
      setCopiedPrompts(newCopied);
      setTimeout(() => {
        setCopiedPrompts(prev => {
          const updated = new Set(prev);
          updated.delete(stepIndex);
          return updated;
        });
      }, 2000);
    }
  };

  if (!steps.length) return null;

  return (
    <div className={`relative bg-white/60 backdrop-blur-sm rounded-3xl p-8 border border-slate-200/60 ${className}`}>
      {/* Goal Center */}
      <div className="flex flex-col items-center mb-12">
        <div className="relative">
          <div className="w-20 h-20 bg-gradient-to-br from-blue-500 to-indigo-600 rounded-3xl flex items-center justify-center shadow-xl shadow-blue-500/25 mb-4 relative overflow-hidden">
            <Target className="w-10 h-10 text-white relative z-10" />
            {/* Animated background pulse */}
            <div className="absolute inset-0 bg-gradient-to-br from-blue-400 to-indigo-500 rounded-3xl animate-pulse opacity-50"></div>
          </div>
          {/* Starting connector to first step */}
          {steps.length > 0 && (
            <div className="absolute left-1/2 -bottom-2 transform -translate-x-1/2">
              <FlowConnector isFirst={true} />
            </div>
          )}
        </div>
        <h3 className="text-xl font-semibold text-slate-800 text-center max-w-md">
          {goal || 'Your AI Journey'}
        </h3>
        <p className="text-sm text-slate-600 mt-2">Click on steps to explore tools</p>
        
        {/* Shared Tools Toggle */}
        {toolAnalysis.sharedTools.length > 0 && (
          <div className="mt-4">
            <button
              onClick={() => setShowSharedTools(!showSharedTools)}
              className="inline-flex items-center gap-2 px-4 py-2 bg-purple-50 hover:bg-purple-100 border border-purple-200 rounded-xl text-purple-700 text-sm font-medium transition-colors"
            >
              <Users className="w-4 h-4" />
              <span>{showSharedTools ? translate('hideSharedTools') : translate('showSharedTools')} {translate('sharedTools')} ({toolAnalysis.sharedTools.length})</span>
            </button>
          </div>
        )}
      </div>

      {/* Shared Tools Section */}
      {showSharedTools && toolAnalysis.sharedTools.length > 0 && (
        <div className="mb-16">
          <div className="text-center mb-8">
            <div className="inline-flex items-center gap-2 px-4 py-2 bg-gradient-to-r from-purple-50 to-pink-50 border border-purple-200 rounded-xl text-purple-700 text-sm font-medium">
              <LinkIcon className="w-4 h-4" />
              <span>{translate('toolsUsedAcrossSteps')}</span>
            </div>
          </div>
          
          <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6 max-w-4xl mx-auto">
            {toolAnalysis.sharedTools.map((sharedTool) => (
              <SharedToolCard
                key={sharedTool.tool.name}
                sharedTool={sharedTool}
                onOpenTool={onOpenTool}
                isHovered={hoveredTool === sharedTool.tool.name}
                onHover={(name) => setHoveredTool(name)}
                onHoverEnd={() => setHoveredTool(null)}
                translate={translate}
              />
            ))}
          </div>
        </div>
      )}

      {/* Steps Flow */}
      <div className="space-y-16">
        {steps.map((step, index) => (
          <div key={step.title} className="relative">
            {/* Step Card */}
            <Card 
              className={`relative z-10 transition-all duration-500 cursor-pointer border-slate-200/60 hover:shadow-2xl hover:-translate-y-2 ${
                activeStep === index 
                  ? 'ring-2 ring-blue-400 shadow-2xl bg-blue-50/50 scale-[1.02]' 
                  : 'hover:bg-white/90'
              }`}
              onClick={() => setActiveStep(activeStep === index ? null : index)}
            >
              <CardContent className="p-6">
                <div className="flex items-center justify-between mb-4">
                  <div className="flex items-center gap-4">
                    {/* Step Number with Glow */}
                    <div className={`relative w-14 h-14 rounded-2xl flex items-center justify-center transition-all duration-300 ${
                      activeStep === index
                        ? 'bg-gradient-to-br from-blue-500 to-indigo-600 shadow-lg shadow-blue-500/30'
                        : 'bg-gradient-to-br from-slate-100 to-slate-200 hover:from-blue-100 hover:to-indigo-100'
                    }`}>
                      <span className={`text-lg font-semibold transition-colors ${
                        activeStep === index ? 'text-white' : 'text-slate-700'
                      }`}>
                        {index + 1}
                      </span>
                      {/* Animated ring */}
                      {activeStep === index && (
                        <div className="absolute inset-0 rounded-2xl border-2 border-blue-400 animate-ping opacity-75"></div>
                      )}
                    </div>
                    <div>
                      <h4 className="text-lg font-semibold text-slate-800">{step.title}</h4>
                      <p className="text-sm text-slate-600 flex items-center gap-2">
                        <Circle className="w-3 h-3 fill-emerald-400 text-emerald-400" />
                        {step.tools.length} tools available
                      </p>
                    </div>
                  </div>
                  <div className="flex items-center gap-2">
                    {/* AI Assistant Button */}
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        togglePrompt(index);
                      }}
                      className={`inline-flex items-center gap-2 px-3 py-2 rounded-xl border transition-all text-sm font-medium ${
                        visiblePrompts.has(index)
                          ? 'bg-amber-100 border-amber-200 text-amber-700 hover:bg-amber-200'
                          : 'bg-amber-50 border-amber-200/60 text-amber-600 hover:bg-amber-100'
                      }`}
                      title={language === 'bg' ? 'ÐŸÑ€ÐµÐ´Ð»Ð¾Ð¶ÐµÐ½Ð¸Ðµ Ð¾Ñ‚ AI Ð°ÑÐ¸ÑÑ‚ÐµÐ½Ñ‚' : 'AI Assistant Suggestion'}
                    >
                      <Lightbulb className="w-4 h-4" />
                      <span className="hidden md:inline">
                        {language === 'bg' ? 'AI Ð°ÑÐ¸ÑÑ‚ÐµÐ½Ñ‚' : 'AI Guide'}
                      </span>
                    </button>
                    <div className={`px-3 py-1 rounded-full text-xs font-medium transition-all ${
                      activeStep === index 
                        ? 'bg-blue-100 text-blue-700'
                        : 'bg-slate-100 text-slate-600'
                    }`}>
                      Step {index + 1}
                    </div>
                    <ChevronRight className={`w-5 h-5 text-slate-400 transition-all duration-300 ${
                      activeStep === index ? 'rotate-90 text-blue-600' : 'hover:text-slate-600'
                    }`} />
                  </div>
                </div>

                {/* Expanded Tools - Only show unique tools for this step */}
                {activeStep === index && (
                  <div className="border-t border-slate-200/60 pt-6 animate-in slide-in-from-top-2 duration-300">
                    {/* AI Prompt Section - Now at top when expanded */}
                    {visiblePrompts.has(index) && (
                      <div className="mb-6">
                        <div className="bg-gradient-to-r from-amber-50 to-orange-50 border border-amber-200/60 rounded-2xl p-6">
                          <div className="flex items-start gap-4">
                            <div className="w-10 h-10 bg-gradient-to-br from-amber-400 to-orange-500 rounded-xl flex items-center justify-center shadow-lg shadow-amber-500/25 flex-shrink-0">
                              <Lightbulb className="w-5 h-5 text-white" />
                            </div>
                            <div className="flex-1">
                              <div className="flex items-center gap-2 mb-3">
                                <h4 className="font-semibold text-amber-800">{translate('assistantSuggestion')}</h4>
                                <Badge variant="secondary" className="text-xs bg-amber-100 text-amber-700 border-amber-200">
                                  {translate('copyPasteReady')}
                                </Badge>
                              </div>
                              
                              <div className="flex items-start gap-3">
                                <div className="flex-1 bg-white/80 border border-amber-200 rounded-xl p-4">
                                  <p className="text-sm text-slate-700 leading-relaxed">
                                    {buildStepPrompt(step.title, step.tools)}
                                  </p>
                                </div>
                                <button 
                                  onClick={(e) => {
                                    e.stopPropagation();
                                    copyPrompt(index, buildStepPrompt(step.title, step.tools));
                                  }} 
                                  className={`h-auto px-4 py-3 rounded-xl border border-amber-200 hover:bg-amber-50 transition-all ${
                                    copiedPrompts.has(index) ? 'bg-green-50 border-green-200 text-green-700' : 'text-amber-700 bg-white'
                                  }`}
                                >
                                  {copiedPrompts.has(index) ? <Check className="w-4 h-4 mr-1" /> : <Copy className="w-4 h-4 mr-1" />}
                                  <span className="text-sm font-medium">{copiedPrompts.has(index) ? translate('copied') : translate('copy')}</span>
                                </button>
                              </div>
                              
                              <p className="text-xs text-amber-700 mt-3">
                                {translate('promptHint')}
                              </p>
                            </div>
                          </div>
                        </div>
                      </div>
                    )}

                    {/* Unique tools for this step */}
                    {toolAnalysis.uniqueToolsByStep.get(index) && toolAnalysis.uniqueToolsByStep.get(index)!.length > 0 && (
                      <div>
                        <h5 className="text-sm font-medium text-slate-600 mb-4 flex items-center gap-2">
                          <Circle className="w-3 h-3 fill-blue-400 text-blue-400" />
                          {translate('stepSpecificTools')}
                        </h5>
                        <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-4 mb-6">
                          {toolAnalysis.uniqueToolsByStep.get(index)!.map((tool, toolIndex) => (
                            <div
                              key={tool.name}
                              className="relative group p-4 rounded-xl border bg-white/90 border-slate-200/60 hover:border-blue-300 hover:shadow-md transition-all duration-200 cursor-pointer"
                              style={{ animationDelay: `${toolIndex * 50}ms` }}
                              onClick={(e) => {
                                e.stopPropagation();
                                onOpenTool(tool);
                              }}
                            >
                              <div className="flex items-center gap-3">
                                <div className="relative w-10 h-10 rounded-xl bg-gradient-to-br from-blue-400 to-indigo-500 flex items-center justify-center shadow-sm group-hover:shadow-md transition-shadow">
                                  {tool.icon ? (
                                    <img 
                                      src={tool.icon} 
                                      alt={tool.name}
                                      className="w-6 h-6 object-contain"
                                      onError={(e) => {
                                        (e.target as HTMLImageElement).style.display = 'none';
                                      }}
                                    />
                                  ) : (
                                    <Sparkles className="w-5 h-5 text-white" />
                                  )}
                                </div>
                                <div className="flex-1 min-w-0">
                                  <p className="font-medium text-slate-800 truncate group-hover:text-blue-700 transition-colors">
                                    {tool.name}
                                  </p>
                                  <Badge variant="secondary" className="text-xs mt-1 bg-blue-100 text-blue-700 border-blue-200">
                                    {translate('aiTool')}
                                  </Badge>
                                </div>
                                <ArrowRight className="w-4 h-4 text-slate-400 group-hover:text-blue-600 transition-colors" />
                              </div>
                            </div>
                          ))}
                        </div>
                      </div>
                    )}

                    {/* Shared tools that apply to this step */}
                    {toolAnalysis.sharedTools.filter(st => st.stepIndices.includes(index)).length > 0 && (
                      <div>
                        <h5 className="text-sm font-medium text-slate-600 mb-4 flex items-center gap-2">
                          <LinkIcon className="w-3 h-3 text-purple-500" />
                          {translate('sharedToolsForStep')}
                        </h5>
                        <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-4">
                          {toolAnalysis.sharedTools
                            .filter(st => st.stepIndices.includes(index))
                            .map((sharedTool, toolIndex) => (
                              <div
                                key={sharedTool.tool.name}
                                className="relative group p-4 rounded-xl border bg-gradient-to-br from-purple-50/50 to-pink-50/50 border-purple-200/60 hover:border-purple-300 hover:shadow-md transition-all duration-200 cursor-pointer"
                                style={{ animationDelay: `${toolIndex * 50}ms` }}
                                onClick={(e) => {
                                  e.stopPropagation();
                                  onOpenTool(sharedTool.tool);
                                }}
                              >
                                <div className="flex items-center gap-3">
                                  <div className="relative w-10 h-10 rounded-xl bg-gradient-to-br from-purple-400 to-pink-500 flex items-center justify-center shadow-sm group-hover:shadow-md transition-shadow">
                                    {sharedTool.tool.icon ? (
                                      <img 
                                        src={sharedTool.tool.icon} 
                                        alt={sharedTool.tool.name}
                                        className="w-6 h-6 object-contain"
                                        onError={(e) => {
                                          (e.target as HTMLImageElement).style.display = 'none';
                                        }}
                                      />
                                    ) : (
                                      <Users className="w-5 h-5 text-white" />
                                    )}
                                  </div>
                                  <div className="flex-1 min-w-0">
                                    <p className="font-medium text-slate-800 truncate group-hover:text-purple-700 transition-colors">
                                      {sharedTool.tool.name}
                                    </p>
                                    <Badge variant="secondary" className="text-xs mt-1 bg-purple-100 text-purple-700 border-purple-200">
                                      {translate('usedInXSteps', { count: sharedTool.stepIndices.length })}
                                    </Badge>
                                  </div>
                                  <ArrowRight className="w-4 h-4 text-slate-400 group-hover:text-purple-600 transition-colors" />
                                </div>
                              </div>
                            ))}
                        </div>
                      </div>
                    )}

                    {/* Message if no tools */}
                    {toolAnalysis.uniqueToolsByStep.get(index)?.length === 0 && 
                     toolAnalysis.sharedTools.filter(st => st.stepIndices.includes(index)).length === 0 && (
                      <div className="text-center py-8 text-slate-500">
                        <Sparkles className="w-8 h-8 mx-auto mb-2 opacity-50" />
                        <p className="text-sm">No tools found for this step</p>
                      </div>
                    )}
                  </div>
                )}
              </CardContent>
            </Card>

            {/* Enhanced Connection to Next Step */}
            {index < steps.length - 1 && (
              <div className="absolute left-1/2 -bottom-8 transform -translate-x-1/2 z-0">
                <FlowConnector 
                  isActive={activeStep === index || activeStep === index + 1}
                  delay={index * 200}
                />
              </div>
            )}
          </div>
        ))}
      </div>

      {/* Enhanced Summary */}
      <div className="mt-12 text-center space-y-4">
        <div className="flex flex-wrap items-center justify-center gap-4">
          <div className="inline-flex items-center gap-3 bg-gradient-to-r from-blue-50 to-indigo-50 border border-blue-200/60 text-blue-700 px-6 py-3 rounded-2xl text-sm font-medium shadow-sm">
            <div className="w-2 h-2 rounded-full bg-blue-400 animate-pulse"></div>
            <Sparkles className="w-4 h-4" />
            <span>
              {Array.from(toolAnalysis.uniqueToolsByStep.values()).reduce((acc, tools) => acc + tools.length, 0)} {translate('uniqueTools')}
            </span>
          </div>
          
          {toolAnalysis.sharedTools.length > 0 && (
            <div className="inline-flex items-center gap-3 bg-gradient-to-r from-purple-50 to-pink-50 border border-purple-200/60 text-purple-700 px-6 py-3 rounded-2xl text-sm font-medium shadow-sm">
              <div className="w-2 h-2 rounded-full bg-purple-400 animate-pulse"></div>
              <Users className="w-4 h-4" />
              <span>
                {toolAnalysis.sharedTools.length} {translate('sharedAcrossSteps')}
              </span>
            </div>
          )}
          
          <div className="inline-flex items-center gap-3 bg-gradient-to-r from-emerald-50 to-green-50 border border-emerald-200/60 text-emerald-700 px-6 py-3 rounded-2xl text-sm font-medium shadow-sm">
            <div className="w-2 h-2 rounded-full bg-emerald-400 animate-pulse"></div>
            <Target className="w-4 h-4" />
            <span>
              {steps.length} {translate('stepsTotal')}
            </span>
          </div>
        </div>
        
        {toolAnalysis.sharedTools.length > 0 && (
          <p className="text-xs text-slate-500 max-w-md mx-auto">
            {translate('sharedToolsHint')}
          </p>
        )}
      </div>
      
      {/* Execution Summary */}
      <ExecutionSummary toolAnalysis={toolAnalysis} steps={steps} translate={translate} />
    </div>
  );
}

// Enhanced Flow Connector Component
function FlowConnector({ isFirst = false, isActive = false, delay = 0 }: { 
  isFirst?: boolean; 
  isActive?: boolean; 
  delay?: number; 
}) {
  return (
    <div className="relative flex flex-col items-center">
      {/* Main Connector Line */}
      <div className={`relative w-1 h-16 rounded-full transition-all duration-500 ${
        isActive 
          ? 'bg-gradient-to-b from-blue-400 via-blue-500 to-indigo-500 shadow-lg shadow-blue-500/30' 
          : 'bg-gradient-to-b from-slate-300 via-slate-400 to-slate-300'
      }`}>
        {/* Animated Flow Dots */}
        <div className={`absolute inset-x-0 transition-opacity duration-500 ${isActive ? 'opacity-100' : 'opacity-0'}`}>
          <div 
            className="absolute w-2 h-2 bg-white rounded-full shadow-sm animate-bounce"
            style={{ 
              left: '50%', 
              transform: 'translateX(-50%)',
              animationDelay: `${delay}ms`,
              animationDuration: '2s',
              animationIterationCount: 'infinite'
            }}
          />
          <div 
            className="absolute w-1.5 h-1.5 bg-blue-200 rounded-full animate-bounce"
            style={{ 
              left: '50%', 
              top: '25%',
              transform: 'translateX(-50%)',
              animationDelay: `${delay + 500}ms`,
              animationDuration: '2s',
              animationIterationCount: 'infinite'
            }}
          />
          <div 
            className="absolute w-1 h-1 bg-blue-100 rounded-full animate-bounce"
            style={{ 
              left: '50%', 
              top: '50%',
              transform: 'translateX(-50%)',
              animationDelay: `${delay + 1000}ms`,
              animationDuration: '2s',
              animationIterationCount: 'infinite'
            }}
          />
        </div>

        {/* Glow Effect */}
        {isActive && (
          <div className="absolute inset-0 w-1 h-16 bg-gradient-to-b from-blue-400 to-indigo-500 rounded-full blur-sm opacity-50 animate-pulse"></div>
        )}
      </div>

      {/* Directional Arrow */}
      <div className={`mt-1 transition-all duration-500 ${
        isActive 
          ? 'text-blue-500 scale-110' 
          : 'text-slate-400 scale-100'
      }`}>
        <ArrowDown className="w-4 h-4" />
      </div>

      {/* Connection Nodes */}
      <div className="absolute -top-2 left-1/2 transform -translate-x-1/2">
        <div className={`w-3 h-3 rounded-full border-2 transition-all duration-300 ${
          isActive 
            ? 'bg-blue-500 border-blue-300 shadow-lg shadow-blue-500/30' 
            : 'bg-white border-slate-300'
        }`} />
      </div>
      
      <div className="absolute -bottom-2 left-1/2 transform -translate-x-1/2">
        <div className={`w-3 h-3 rounded-full border-2 transition-all duration-300 ${
          isActive 
            ? 'bg-blue-500 border-blue-300 shadow-lg shadow-blue-500/30' 
            : 'bg-white border-slate-300'
        }`} />
      </div>

      {/* Side Connection Lines for Visual Appeal */}
      <div className="absolute top-1/2 -left-4 w-3 h-px bg-gradient-to-r from-transparent to-slate-300 transform -translate-y-1/2" />
      <div className="absolute top-1/2 -right-4 w-3 h-px bg-gradient-to-l from-transparent to-slate-300 transform -translate-y-1/2" />
    </div>
  );
}

// Shared Tool Card Component
interface SharedToolCardProps {
  sharedTool: SharedTool;
  onOpenTool: (tool: Tool) => void;
  isHovered: boolean;
  onHover: (name: string) => void;
  onHoverEnd: () => void;
  translate: (key: any, params?: { [key: string]: string | number }) => string;
}

function SharedToolCard({ sharedTool, onOpenTool, isHovered, onHover, onHoverEnd, translate }: SharedToolCardProps) {
  return (
    <Card 
      className={`relative group transition-all duration-300 cursor-pointer border-purple-200/60 bg-gradient-to-br from-purple-50/80 to-pink-50/80 hover:shadow-xl hover:-translate-y-1 ${
        isHovered ? 'ring-2 ring-purple-400 shadow-xl scale-105' : ''
      }`}
      onMouseEnter={() => onHover(sharedTool.tool.name)}
      onMouseLeave={onHoverEnd}
      onClick={() => onOpenTool(sharedTool.tool)}
    >
      <CardContent className="p-6">
        <div className="flex items-center gap-4 mb-4">
          <div className="relative w-16 h-16 rounded-2xl bg-gradient-to-br from-purple-500 to-pink-600 flex items-center justify-center shadow-lg group-hover:shadow-xl transition-shadow">
            {sharedTool.tool.icon ? (
              <img 
                src={sharedTool.tool.icon} 
                alt={sharedTool.tool.name}
                className="w-8 h-8 object-contain"
                onError={(e) => {
                  (e.target as HTMLImageElement).style.display = 'none';
                }}
              />
            ) : (
              <Users className="w-8 h-8 text-white" />
            )}
            {/* Connection indicator */}
            <div className="absolute -top-1 -right-1 w-6 h-6 bg-gradient-to-br from-orange-400 to-red-500 rounded-full flex items-center justify-center shadow-md">
              <LinkIcon className="w-3 h-3 text-white" />
            </div>
          </div>
          <div className="flex-1">
            <h4 className="font-semibold text-slate-800 mb-1 group-hover:text-purple-700 transition-colors">
              {sharedTool.tool.name}
            </h4>
            <Badge variant="secondary" className="bg-purple-100 text-purple-700 border-purple-200 text-xs">
              {translate('usedInXSteps', { count: sharedTool.stepIndices.length })}
            </Badge>
          </div>
        </div>

        <div className="space-y-2">
          <p className="text-xs font-medium text-slate-600 mb-2">{translate('usedInSteps')}:</p>
          <div className="flex flex-wrap gap-2">
            {sharedTool.stepIndices.map((stepIndex, idx) => (
              <div 
                key={stepIndex}
                className="inline-flex items-center gap-1 px-2 py-1 bg-white/80 border border-purple-200 rounded-lg text-xs"
              >
                <div className="w-4 h-4 bg-gradient-to-br from-purple-400 to-pink-500 rounded text-white flex items-center justify-center text-xs font-semibold">
                  {stepIndex + 1}
                </div>
                <span className="text-slate-600 truncate max-w-[80px]" title={sharedTool.stepTitles[idx]}>
                  {sharedTool.stepTitles[idx]}
                </span>
              </div>
            ))}
          </div>
        </div>

        {/* Connection lines visualization */}
        <div className="mt-4 pt-4 border-t border-purple-200/60">
          <div className="flex items-center justify-center">
            <div className="flex items-center gap-1">
              {sharedTool.stepIndices.map((_, idx) => (
                <div key={idx} className="flex items-center">
                  <div className="w-2 h-2 bg-purple-400 rounded-full animate-pulse" style={{ animationDelay: `${idx * 200}ms` }} />
                  {idx < sharedTool.stepIndices.length - 1 && (
                    <div className="w-4 h-px bg-gradient-to-r from-purple-300 to-pink-300 mx-1" />
                  )}
                </div>
              ))}
            </div>
          </div>
        </div>

        {sharedTool.tool.link && (
          <div className="mt-4">
            <Badge variant="secondary" className="text-xs bg-emerald-100 text-emerald-700 border-emerald-200">
              Available
            </Badge>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

// Step Tool Card Component
interface StepToolCardProps {
  tool: Tool;
  toolIndex: number;
  hoveredTool: string | null;
  setHoveredTool: (name: string | null) => void;
  onOpenTool: (tool: Tool) => void;
  stepIndex: number;
  stepTitle: string;
  allStepTools: Tool[];
  showPrompt?: () => void;
  translate: (key: any, params?: { [key: string]: string | number }) => string;
}

function StepToolCard({ tool, toolIndex, hoveredTool, setHoveredTool, onOpenTool, showPrompt, translate }: StepToolCardProps) {
  return (
    <div
      className={`relative group p-4 rounded-xl border transition-all duration-200 ${
        hoveredTool === tool.name 
          ? 'bg-blue-50/80 border-blue-300 shadow-md scale-105' 
          : 'bg-white/90 border-slate-200/60 hover:border-blue-200 hover:shadow-sm'
      }`}
      style={{ animationDelay: `${toolIndex * 50}ms` }}
      onMouseEnter={() => setHoveredTool(tool.name)}
      onMouseLeave={() => setHoveredTool(null)}
    >
      <div className="flex items-center gap-3 mb-3">
        <div className="relative w-10 h-10 rounded-xl bg-gradient-to-br from-blue-400 to-indigo-500 flex items-center justify-center shadow-sm group-hover:shadow-md transition-shadow">
          {tool.icon ? (
            <img 
              src={tool.icon} 
              alt={tool.name}
              className="w-6 h-6 object-contain"
              onError={(e) => {
                (e.target as HTMLImageElement).style.display = 'none';
              }}
            />
          ) : (
            <Sparkles className="w-5 h-5 text-white" />
          )}
          {/* Hover glow effect */}
          {hoveredTool === tool.name && (
            <div className="absolute inset-0 rounded-xl bg-blue-400 animate-ping opacity-30"></div>
          )}
        </div>
        <div className="flex-1 min-w-0">
          <p className="font-medium text-slate-800 truncate group-hover:text-blue-700 transition-colors">
            {tool.name}
          </p>
          {tool.link && (
            <Badge variant="secondary" className="text-xs mt-1 bg-emerald-100 text-emerald-700 border-emerald-200">
              Available
            </Badge>
          )}
        </div>
      </div>
      
      {/* Action buttons */}
      <div className="flex gap-2">
        {tool.link ? (
          <button
            onClick={(e) => {
              e.stopPropagation();
              onOpenTool(tool);
            }}
            className="flex-1 text-center bg-gradient-to-r from-red-500 to-pink-500 hover:from-red-600 hover:to-pink-600 text-white rounded-xl py-2 px-3 text-sm font-medium shadow-lg shadow-red-500/25 transition-all duration-200"
          >
            {translate('openTool')}
          </button>
        ) : (
          <button disabled className="flex-1 text-center bg-slate-100 text-slate-400 rounded-xl py-2 px-3 text-sm font-medium">
            {translate('noLink')}
          </button>
        )}
        
        <button
          onClick={(e) => {
            e.stopPropagation();
            showPrompt?.();
          }}
          className="bg-gradient-to-r from-amber-400 to-orange-500 hover:from-amber-500 hover:to-orange-600 text-white rounded-xl py-2 px-3 text-sm font-medium shadow-lg shadow-amber-500/25 transition-all duration-200 flex items-center gap-1"
          title={translate('getAIHelp')}
        >
          <Lightbulb className="w-4 h-4" />
        </button>
      </div>
    </div>
  );
}

// Execution Summary Component
interface ExecutionSummaryProps {
  toolAnalysis: ToolAnalysis;
  steps: Group[];
  translate: (key: any, params?: { [key: string]: string | number }) => string;
}

function ExecutionSummary({ toolAnalysis, steps, translate }: ExecutionSummaryProps) {
  return (
    <div className="mt-16 bg-gradient-to-br from-indigo-50 via-blue-50 to-cyan-50 rounded-3xl p-8 border border-indigo-200/50 shadow-lg shadow-indigo-500/10">
      {/* Header */}
      <div className="text-center mb-8">
        <div className="inline-flex items-center gap-3 mb-4">
          <div className="w-12 h-12 bg-gradient-to-br from-indigo-500 to-blue-600 rounded-2xl flex items-center justify-center shadow-lg shadow-indigo-500/30">
            <CheckCircle2 className="w-6 h-6 text-white" />
          </div>
          <div>
            <h3 className="text-2xl font-bold text-slate-800">{translate('executionSummary')}</h3>
            <p className="text-sm text-slate-600">{translate('executionSummarySubtitle')}</p>
          </div>
        </div>
      </div>

      {/* Step-by-step recommendations */}
      <div className="space-y-6">
        {steps.map((step, index) => {
          const stepSharedTools = toolAnalysis.sharedTools.filter(st => st.stepIndices.includes(index));
          const stepUniqueTools = toolAnalysis.uniqueToolsByStep.get(index) || [];
          const totalStepTools = stepSharedTools.length + stepUniqueTools.length;
          
          if (totalStepTools === 0) return null;
          
          return (
            <div key={step.title} className="relative group">
              <div className="flex items-start gap-4 p-6 rounded-2xl bg-white/80 border border-indigo-200/60 hover:border-indigo-300/80 transition-all duration-300 hover:shadow-lg">
                {/* Step indicator */}
                <div className="flex-shrink-0">
                  <div className="w-10 h-10 bg-gradient-to-br from-indigo-500 to-blue-600 rounded-xl flex items-center justify-center shadow-md">
                    <span className="text-white font-bold text-sm">{index + 1}</span>
                  </div>
                </div>
                
                {/* Content */}
                <div className="flex-1 min-w-0">
                  <h4 className="font-semibold text-slate-800 mb-2">{step.title}</h4>
                  
                  {/* Tool recommendation text */}
                  <div className="mb-4">
                    <div className="inline-flex items-center gap-2 px-3 py-1.5 bg-gradient-to-r from-emerald-50 to-green-50 border border-emerald-200 rounded-lg">
                      <MapPin className="w-4 h-4 text-emerald-600" />
                      <span className="text-sm font-medium text-emerald-700">
                        {totalStepTools === 1 
                          ? `${translate('forStep')} ${index + 1}, ${translate('useTheseTool')}`
                          : `${translate('forStep')} ${index + 1}, ${translate('useTheseTools')}`
                        }:
                      </span>
                    </div>
                  </div>
                  
                  {/* Tools grid */}
                  <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-3">
                    {/* Unique tools */}
                    {stepUniqueTools.map((tool) => (
                      <div key={tool.name} className="flex items-center gap-2 p-2 rounded-lg bg-blue-50/50 border border-blue-200/50">
                        <div className="w-6 h-6 rounded bg-gradient-to-br from-blue-400 to-indigo-500 flex items-center justify-center flex-shrink-0">
                          {tool.icon ? (
                            <img 
                              src={tool.icon} 
                              alt={tool.name}
                              className="w-4 h-4 object-contain"
                              onError={(e) => {
                                (e.target as HTMLImageElement).style.display = 'none';
                              }}
                            />
                          ) : (
                            <Sparkles className="w-3 h-3 text-white" />
                          )}
                        </div>
                        <span className="text-sm font-medium text-slate-700 truncate">{tool.name}</span>
                      </div>
                    ))}
                    
                    {/* Shared tools */}
                    {stepSharedTools.map((sharedTool) => (
                      <div key={sharedTool.tool.name} className="flex items-center gap-2 p-2 rounded-lg bg-purple-50/50 border border-purple-200/50">
                        <div className="w-6 h-6 rounded bg-gradient-to-br from-purple-400 to-pink-500 flex items-center justify-center flex-shrink-0 relative">
                          {sharedTool.tool.icon ? (
                            <img 
                              src={sharedTool.tool.icon} 
                              alt={sharedTool.tool.name}
                              className="w-4 h-4 object-contain"
                              onError={(e) => {
                                (e.target as HTMLImageElement).style.display = 'none';
                              }}
                            />
                          ) : (
                            <Users className="w-3 h-3 text-white" />
                          )}
                          <div className="absolute -top-1 -right-1 w-3 h-3 bg-orange-400 rounded-full flex items-center justify-center">
                            <LinkIcon className="w-1.5 h-1.5 text-white" />
                          </div>
                        </div>
                        <span className="text-sm font-medium text-slate-700 truncate">{sharedTool.tool.name}</span>
                      </div>
                    ))}
                  </div>
                </div>
                
                {/* Step total indicator */}
                <div className="flex-shrink-0">
                  <div className="px-3 py-1 bg-gradient-to-r from-slate-100 to-slate-200 rounded-full">
                    <span className="text-xs font-medium text-slate-600">
                      {totalStepTools} {totalStepTools === 1 ? 'tool' : 'tools'}
                    </span>
                  </div>
                </div>
              </div>
            </div>
          );
        })}
      </div>
      
      {/* Bottom summary */}
      <div className="mt-8 pt-6 border-t border-indigo-200/60">
        <div className="text-center">
          <div className="inline-flex items-center gap-2 px-4 py-2 bg-gradient-to-r from-indigo-100 to-blue-100 border border-indigo-200 rounded-xl">
            <CheckCircle2 className="w-4 h-4 text-indigo-600" />
            <span className="text-sm font-medium text-indigo-700">
              {translate('toolRecommendation')} â€¢ {steps.length} {translate('stepsTotal')} â€¢ {Array.from(toolAnalysis.uniqueToolsByStep.values()).reduce((acc, tools) => acc + tools.length, 0) + toolAnalysis.sharedTools.length} tools total
            </span>
          </div>
        </div>
      </div>
    </div>
  );
}
