import { useEffect, useMemo, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Search, ArrowLeft } from 'lucide-react';
import { Card, CardContent } from '../components/ui/card';
import { Input } from '../components/ui/input';
import { Badge } from '../components/ui/badge';

const API_BASE =
  (import.meta as any).env?.VITE_API_BASE ||
  (window as any).__API_BASE__ ||
  'http://127.0.0.1:8000';

type Category = {
  id: string;           // cap / slug от бекенда (например "code-development")
  label: string;        // четливо име (ако няма в отговора, ще го генерираме)
  count?: number;       // брой инструменти
  description?: string; // по желание
  emoji?: string;       // по желание
  gradient?: string;    // по желание
};

function nicifyLabel(id: string) {
  return id
    .replace(/^cap:/, '')
    .replace(/[-_]+/g, ' ')
    .replace(/\b\w/g, (m) => m.toUpperCase());
}

export default function Categories() {
  const navigate = useNavigate();
  const [search, setSearch] = useState('');
  const [loading, setLoading] = useState(true);
  const [categories, setCategories] = useState<Category[]>([]);

  useEffect(() => {
    let cancelled = false;
    (async () => {
      try {
        const r = await fetch(`${API_BASE}/catalog/categories`);
        const data = await r.json();

        // Поддържаме няколко възможни форми на отговор:
        const arr: any[] = data?.categories || data || [];
        const mapped: Category[] = arr.map((c: any) => {
          const id = c.id || c.cap || c.slug || c.name;
          return {
            id,
            label: c.label || nicifyLabel(id),
            count: c.count ?? c.tools_count ?? c.tools?.length ?? 0,
            description: c.description || '',
            // по избор: ако бекендът дава emoji/gradient – взимаме ги
            emoji: c.emoji || '✨',
            gradient: c.gradient || 'from-blue-500 to-indigo-500',
          };
        });

        if (!cancelled) {
          setCategories(mapped);
          setLoading(false);
        }
      } catch (e) {
        console.error('Failed to fetch categories', e);
        if (!cancelled) setLoading(false);
      }
    })();
    return () => { cancelled = true; };
  }, []);

  const filtered = useMemo(() => {
    const term = search.trim().toLowerCase();
    if (!term) return categories;
    return categories.filter(
      (c) =>
        c.label.toLowerCase().includes(term) ||
        (c.description || '').toLowerCase().includes(term) ||
        c.id.toLowerCase().includes(term)
    );
  }, [categories, search]);

  return (
    <main className="max-w-6xl mx-auto px-4 py-8 pb-24 md:pb-12">
      <div className="flex items-center justify-between mb-8">
        <div className="flex items-center gap-3">
          <button
            onClick={() => navigate(-1)}
            className="inline-flex items-center gap-2 text-slate-600 hover:text-blue-600 transition-colors p-2 -ml-2 rounded-xl"
          >
            <ArrowLeft className="w-4 h-4" />
            <span className="font-medium">Назад</span>
          </button>
          <div className="w-px h-6 bg-slate-300" />
          <div>
            <h1 className="text-3xl text-slate-800 font-bold">AI Категории Инструменти</h1>
            <p className="text-slate-600 text-sm mt-1">
              Разгледайте нашата курирана колекция от AI инструменти, организирани по предназначение и функционалност.
            </p>
          </div>
        </div>
      </div>

      {/* Search */}
      <div className="mb-8">
        <div className="relative max-w-md">
          <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400 w-5 h-5" />
          <Input
            placeholder="Търсене в категории..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            className="pl-12 bg-white/80 border-slate-200/50 h-12 rounded-2xl backdrop-blur-sm font-medium"
          />
        </div>
      </div>

      {/* Grid */}
      {loading ? (
        <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {Array.from({ length: 9 }).map((_, i) => (
            <div key={i} className="h-40 bg-slate-100 rounded-3xl animate-pulse" />
          ))}
        </div>
      ) : filtered.length === 0 ? (
        <div className="text-center py-20">
          <div className="w-20 h-20 bg-slate-100 rounded-3xl flex items-center justify-center mx-auto mb-6">
            <Search className="w-10 h-10 text-slate-400" />
          </div>
          <h3 className="text-slate-800 mb-3 font-bold text-xl">Няма намерени категории</h3>
          <p className="text-slate-600">Опитайте с друга дума за търсене.</p>
        </div>
      ) : (
        <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {filtered.map((category) => (
            <Card
              key={category.id}
              onClick={() => navigate(`/categories/${encodeURIComponent(category.id)}`)}
              className="group border-slate-200/50 hover:shadow-2xl transition-all duration-300 hover:-translate-y-2 bg-white/90 backdrop-blur-sm cursor-pointer overflow-hidden"
            >
              <CardContent className="p-6 relative">
                <div className={`absolute inset-0 bg-gradient-to-br ${category.gradient} opacity-5 group-hover:opacity-10 transition-opacity duration-300`} />
                <div className="relative">
                  <div className="flex items-start justify-between mb-4">
                    <div className={`w-16 h-16 rounded-3xl bg-gradient-to-br ${category.gradient} flex items-center justify-center shadow-lg group-hover:scale-110 transition-transform duration-300`}>
                      <span className="text-2xl">{category.emoji || '✨'}</span>
                    </div>
                    <div className="text-right">
                      <div className="text-xs text-slate-500">{category.count ?? 0}</div>
                      <div className="text-xs text-slate-400">инструмента в тази категория</div>
                    </div>
                  </div>
                  <h3 className="text-xl font-bold text-slate-800 mb-2 group-hover:text-slate-900 transition-colors">
                    {category.label}
                  </h3>
                  {category.description ? (
                    <p className="text-sm text-slate-600 mb-4 leading-relaxed">{category.description}</p>
                  ) : null}
                  <div className="flex items-center justify-between">
                    <Badge variant="secondary" className={`text-xs font-medium px-3 py-1 bg-gradient-to-r ${category.gradient} text-white border-0`}>
                      Разгледай категория
                    </Badge>
                    <ArrowLeft className="w-4 h-4 text-slate-400 rotate-180 group-hover:translate-x-1 transition-transform duration-300" />
                  </div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}
    </main>
  );
}
