import i18n from 'i18next';
import { initReactI18next } from 'react-i18next';
import LanguageDetector from 'i18next-browser-languagedetector';
import Backend from 'i18next-http-backend';

i18n
  .use(Backend) 
  // Автоматично разпознаване на езика
  .use(LanguageDetector)
  // React интеграция
  .use(initReactI18next)
  // Конфигурация
  .init({
    // Поддържани езици
    supportedLngs: ['en', 'bg', 'es', 'de', 'fr'],
    
    // Fallback език ако превод липсва
    fallbackLng: 'en',
    
    // Дебъг режим за development
    debug: false,
    
    // Namespace (може да имаш множество)
    ns: ['translation'],
    defaultNS: 'translation',
    
    // Кеширане
    load: 'languageOnly', // 'en' вместо 'en-US'
    
    // Interpolation настройки
    interpolation: {
      escapeValue: false, // React вече escape-ва
    },
    
    // Backend конфигурация за зареждане на файлове
    backend: {
      loadPath: '/locales/{{lng}}/{{ns}}.json',
    },
    
    // Language detector настройки
    detection: {
      // Ред на проверка
      order: ['localStorage', 'navigator'],
      
      // Кеширане в localStorage
      caches: ['localStorage'],
      
      // localStorage key
      lookupLocalStorage: 'i18nextLng',
    },
    
    // React specific
    react: {
      useSuspense: false, // За да избегнем Suspense проблеми
    },
  });

export default i18n;
