import { useEffect, useMemo, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Search, ArrowLeft } from 'lucide-react';
import { Card, CardContent } from '../components/ui/card';
import { Input } from '../components/ui/input';
import { Badge } from '../components/ui/badge';

const API_BASE =
  (import.meta as any).env?.VITE_API_BASE ||
  (window as any).__API_BASE__ ||
  'http://127.0.0.1:8000';

type Category = {
  id: string;           // cap:* slug –æ—Ç –±–µ–∫–µ–Ω–¥–∞
  label: string;        // —á–µ—Ç–ª–∏–≤–æ –∏–º–µ
  count?: number;       // –±—Ä–æ–π –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–∏
  description?: string; // –æ–ø–∏—Å–∞–Ω–∏–µ
  emoji?: string;       // –µ–º–æ–¥–∂–∏
  gradient?: string;    // –≥—Ä–∞–¥–∏–µ–Ω—Ç
};

// –ú–∞–ø–∏–Ω–≥ –æ—Ç cap:* —Ç–∞–≥–æ–≤–µ –∫—ä–º —Ñ—Ä–æ–Ω—Ç–µ–Ω–¥ –¥–∞–Ω–Ω–∏
const FRONTEND_CATEGORY_MAPPING: Record<string, any> = {
  "cap:text-explain": {
    emoji: "ü§ñ",
    gradient: "from-blue-500 to-indigo-600",
    description: "–£–º–Ω–∏ –ø–æ–º–æ—â–Ω–∏—Ü–∏ –∏ –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–∏ –∑–∞ –ø–æ–≤–∏—à–∞–≤–∞–Ω–µ –Ω–∞ –ø—Ä–æ–¥—É–∫—Ç–∏–≤–Ω–æ—Å—Ç—Ç–∞"
  },
  "cap:text-edit": {
    emoji: "‚úçÔ∏è", 
    gradient: "from-emerald-500 to-teal-600",
    description: "–°—ä–∑–¥–∞–≤–∞–Ω–µ, —Ä–µ–¥–∞–∫—Ç–∏—Ä–∞–Ω–µ –∏ –ø–æ–¥–æ–±—Ä—è–≤–∞–Ω–µ –Ω–∞ —Ç–µ–∫—Å—Ç–æ–≤–æ —Å—ä–¥—ä—Ä–∂–∞–Ω–∏–µ"
  },
  "cap:text-summarize": {
    emoji: "üìù",
    gradient: "from-amber-500 to-orange-600", 
    description: "–†–µ–∑—é–º–∏—Ä–∞–Ω–µ –∏ –∏–∑–≤–ª–∏—á–∞–Ω–µ –Ω–∞ –∫–ª—é—á–æ–≤–∞ –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è"
  },
  "cap:image-generate": {
    emoji: "üé®",
    gradient: "from-purple-500 to-pink-600", 
    description: "–ì–µ–Ω–µ—Ä–∏—Ä–∞–Ω–µ, —Ä–µ–¥–∞–∫—Ç–∏—Ä–∞–Ω–µ –∏ –¥–∏–∑–∞–π–Ω –Ω–∞ –≤–∏–∑—É–∞–ª–Ω–æ —Å—ä–¥—ä—Ä–∂–∞–Ω–∏–µ"
  },
  "cap:image-edit": {
    emoji: "üñºÔ∏è",
    gradient: "from-violet-500 to-purple-600",
    description: "–†–µ–¥–∞–∫—Ç–∏—Ä–∞–Ω–µ –∏ –æ–±—Ä–∞–±–æ—Ç–∫–∞ –Ω–∞ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è"
  },
  "cap:video-generate": {
    emoji: "üé¨",
    gradient: "from-red-500 to-orange-600",
    description: "–°—ä–∑–¥–∞–≤–∞–Ω–µ –∏ —Ä–µ–¥–∞–∫—Ç–∏—Ä–∞–Ω–µ –Ω–∞ –≤–∏–¥–µ–æ –∏ 3D —Å—ä–¥—ä—Ä–∂–∞–Ω–∏–µ"
  },
  "cap:video-edit": {
    emoji: "üéûÔ∏è",
    gradient: "from-pink-500 to-red-600",
    description: "–†–µ–¥–∞–∫—Ç–∏—Ä–∞–Ω–µ –∏ –º–æ–Ω—Ç–∞–∂ –Ω–∞ –≤–∏–¥–µ–æ"
  },
  "cap:voice-generate": {
    emoji: "üéµ",
    gradient: "from-amber-500 to-yellow-600",
    description: "–ì–µ–Ω–µ—Ä–∏—Ä–∞–Ω–µ –Ω–∞ –∑–≤—É–∫, –º—É–∑–∏–∫–∞ –∏ –≥–ª–∞—Å–æ–≤–∏ –∑–∞–ø–∏—Å–∏"
  },
  "cap:audio-transcribe": {
    emoji: "üéôÔ∏è",
    gradient: "from-cyan-500 to-blue-500",
    description: "–¢—Ä–∞–Ω—Å–∫—Ä–∏–ø—Ü–∏—è –∏ –æ–±—Ä–∞–±–æ—Ç–∫–∞ –Ω–∞ –∞—É–¥–∏–æ"
  },
  "cap:research-web": {
    emoji: "üîç", 
    gradient: "from-cyan-500 to-blue-600",
    description: "–£–µ–± —Ç—ä—Ä—Å–µ–Ω–µ, –∏–∑—Å–ª–µ–¥–≤–∞–Ω–∏—è –∏ –∞–Ω–∞–ª–∏–∑ –Ω–∞ –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è"
  },
  "cap:automate-workflow": {
    emoji: "‚ö°",
    gradient: "from-violet-500 to-purple-600",
    description: "–ê–≤—Ç–æ–º–∞—Ç–∏–∑–∞—Ü–∏—è –Ω–∞ –ø—Ä–æ—Ü–µ—Å–∏ –∏ —Ä–∞–±–æ—Ç–Ω–∏ –ø–æ—Ç–æ—Ü–∏"
  },
  "cap:integrations": {
    emoji: "üîó",
    gradient: "from-indigo-500 to-purple-600",
    description: "–ò–Ω—Ç–µ–≥—Ä–∞—Ü–∏–∏ –º–µ–∂–¥—É —Ä–∞–∑–ª–∏—á–Ω–∏ –ø–ª–∞—Ç—Ñ–æ—Ä–º–∏ –∏ —É—Å–ª—É–≥–∏"
  },
  "cap:slide-generate": {
    emoji: "üìä",
    gradient: "from-teal-600 to-emerald-700",
    description: "–°—ä–∑–¥–∞–≤–∞–Ω–µ –Ω–∞ –ø—Ä–µ–∑–µ–Ω—Ç–∞—Ü–∏–∏ –∏ –≥—Ä–∞—Ñ–∏–∫–∏"
  },
  "cap:doc-read-pdf": {
    emoji: "üìÑ",
    gradient: "from-slate-500 to-gray-600",
    description: "–ß–µ—Ç–µ–Ω–µ, –∞–Ω–∞–ª–∏–∑ –∏ –æ–±—Ä–∞–±–æ—Ç–∫–∞ –Ω–∞ –¥–æ–∫—É–º–µ–Ω—Ç–∏"
  }
};

export default function Categories() {
  const navigate = useNavigate();
  const [search, setSearch] = useState('');
  const [loading, setLoading] = useState(true);
  const [categories, setCategories] = useState<Category[]>([]);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    let cancelled = false;
    setLoading(true);
    setError(null);

    console.log('üîÑ Starting categories fetch...');
    console.log('üîó API_BASE:', API_BASE);
    console.log('üåê Full URL:', `${API_BASE}/catalog/categories`);

    (async () => {
      try {
        console.log('üì° Making fetch request...');
        const r = await fetch(`${API_BASE}/catalog/categories`, {
          method: 'GET',
          headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
          },
          mode: 'cors',
          credentials: 'omit'
        });

        console.log('üìä Response received:', {
          status: r.status,
          statusText: r.statusText,
          ok: r.ok,
          headers: Object.fromEntries(r.headers.entries())
        });

        if (!r.ok) {
          const errorText = await r.text();
          console.error('‚ùå API Error Response:', errorText);
          throw new Error(`HTTP ${r.status}: ${r.statusText}`);
        }

        console.log('üîÑ Parsing JSON...');
        const data = await r.json();
        console.log('‚úÖ Raw API Response:', data);
        console.log('üìù Categories array:', data?.categories);

        if (!data?.categories) {
          console.error('‚ùå No categories property in response');
          throw new Error('Invalid response format: missing categories');
        }

        // API shape: { categories: [{id, label, count}, ...] }
        const arr: any[] = data.categories || [];
        console.log('üìã Processing categories:', arr.length, 'items');

        const mapped: Category[] = arr.map((c: any, index: number) => {
          console.log(`üîÑ Processing category ${index}:`, c);
          
          const id = c.id; // cap:* —Ç–∞–≥–æ–≤–µ –æ—Ç –±–µ–∫–µ–Ω–¥–∞
          const label = c.label; // CAP_DISPLAY –æ—Ç catalog_router.py
          
          // –ú–∞–ø–≤–∞–º–µ –∫—ä–º —Ñ—Ä–æ–Ω—Ç–µ–Ω–¥ –¥–∞–Ω–Ω–∏
          const mapping = FRONTEND_CATEGORY_MAPPING[id] || {
            emoji: "‚ú®",
            gradient: "from-slate-500 to-gray-600",
            description: "–°–ø–µ—Ü–∏–∞–ª–Ω–∏ –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–∏ –∑–∞ –∫–æ–Ω–∫—Ä–µ—Ç–Ω–∏ –æ–±–ª–∞—Å—Ç–∏"
          };

          const result = {
            id,
            label,
            count: c.count ?? 0,
            description: mapping.description,
            emoji: mapping.emoji,
            gradient: mapping.gradient,
          };

          console.log(`‚úÖ Mapped category ${index}:`, result);
          return result;
        });

        console.log('üéâ Final mapped categories:', mapped);

        if (!cancelled) {
          console.log('üì¶ Setting categories state...');
          setCategories(mapped);
          setLoading(false);
          console.log('‚úÖ Categories loaded successfully!');
        } else {
          console.log('‚ö†Ô∏è Request was cancelled');
        }
      } catch (e) {
        console.error('üí• Categories fetch failed:', e);
        const errorObj = e as Error;
        console.error('üìã Error details:', {
          name: errorObj.name,
          message: errorObj.message,
          stack: errorObj.stack
        });
        
        if (!cancelled) {
          setError(`–ù–µ—É—Å–ø–µ—à–Ω–æ –∑–∞—Ä–µ–∂–¥–∞–Ω–µ –Ω–∞ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏—Ç–µ: ${(e as Error).message}`);
          setLoading(false);
        }
      }
    })();

    return () => { 
      console.log('üßπ Cleanup: cancelling request');
      cancelled = true; 
    };
  }, []);

  const filtered = useMemo(() => {
    const term = search.trim().toLowerCase();
    if (!term) return categories;
    return categories.filter(
      (c) =>
        c.label.toLowerCase().includes(term) ||
        (c.description || '').toLowerCase().includes(term) ||
        c.id.toLowerCase().includes(term)
    );
  }, [categories, search]);

  const totalTools = categories.reduce((sum, cat) => sum + (cat.count || 0), 0);

  return (
    <main className="max-w-6xl mx-auto px-4 py-8 pb-24 md:pb-12">
      <div className="flex items-center justify-between mb-8">
        <div className="flex items-center gap-3">
          <button
          onClick={() => navigate(`/categories/${encodeURIComponent(category.id)}`)}
            className="inline-flex items-center gap-2 text-slate-600 hover:text-blue-600 transition-colors p-2 -ml-2 rounded-xl"
          >
            <ArrowLeft className="w-4 h-4" />
            <span className="font-medium">–ù–∞–∑–∞–¥</span>
          </button>
          <div className="w-px h-6 bg-slate-300" />
          <div>
            <h1 className="text-3xl text-slate-800 font-bold">AI –ö–∞—Ç–µ–≥–æ—Ä–∏–∏ –ò–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–∏</h1>
            <p className="text-slate-600 text-sm mt-1">
              –†–∞–∑–≥–ª–µ–¥–∞–π—Ç–µ –Ω–∞—à–∞—Ç–∞ –∫–æ–ª–µ–∫—Ü–∏—è –æ—Ç AI –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–∏, –æ—Ä–≥–∞–Ω–∏–∑–∏—Ä–∞–Ω–∏ –ø–æ –ø—Ä–µ–¥–Ω–∞–∑–Ω–∞—á–µ–Ω–∏–µ –∏ —Ñ—É–Ω–∫—Ü–∏–æ–Ω–∞–ª–Ω–æ—Å—Ç.
            </p>
          </div>
        </div>
      </div>

      {/* Search */}
      <div className="mb-8">
        <div className="relative max-w-md">
          <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400 w-5 h-5" />
          <Input
            placeholder="–¢—ä—Ä—Å–µ–Ω–µ –≤ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            className="pl-12 bg-white/80 border-slate-200/50 h-12 rounded-2xl backdrop-blur-sm font-medium"
          />
        </div>
      </div>

      {/* Error State */}
      {error && (
        <div className="text-center py-10">
          <div className="text-red-600 font-medium mb-4 p-4 bg-red-50 rounded-2xl border border-red-200 max-w-2xl mx-auto">
            {error}
          </div>
          <button 
            onClick={() => window.location.reload()} 
            className="text-blue-600 hover:underline"
          >
            –û–ø–∏—Ç–∞–π—Ç–µ –æ—Ç–Ω–æ–≤–æ
          </button>
        </div>
      )}

      {/* Loading State */}
      {loading && !error && (
        <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {Array.from({ length: 9 }).map((_, i) => (
            <div key={i} className="h-40 bg-slate-100 rounded-3xl animate-pulse" />
          ))}
        </div>
      )}

      {/* Empty State */}
      {!loading && !error && filtered.length === 0 && (
        <div className="text-center py-20">
          <div className="w-20 h-20 bg-slate-100 rounded-3xl flex items-center justify-center mx-auto mb-6">
            <Search className="w-10 h-10 text-slate-400" />
          </div>
          <h3 className="text-slate-800 mb-3 font-bold text-xl">–ù—è–º–∞ –Ω–∞–º–µ—Ä–µ–Ω–∏ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏</h3>
          <p className="text-slate-600">–û–ø–∏—Ç–∞–π—Ç–µ —Å –¥—Ä—É–≥–∞ –¥—É–º–∞ –∑–∞ —Ç—ä—Ä—Å–µ–Ω–µ.</p>
        </div>
      )}

      {/* Categories Grid */}
      {!loading && !error && filtered.length > 0 && (
        <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {filtered.map((category) => (
            <Card
              key={category.id}
              onClick={() => {
                window.location.hash = `#/categories/${encodeURIComponent(category.id)}`;
              }}
              className="group border-slate-200/50 hover:shadow-2xl transition-all duration-300 hover:-translate-y-2 bg-white/90 backdrop-blur-sm cursor-pointer overflow-hidden"
            >
              <CardContent className="p-6 relative">
                <div className={`absolute inset-0 bg-gradient-to-br ${category.gradient} opacity-5 group-hover:opacity-10 transition-opacity duration-300`} />
                <div className="relative">
                  <div className="flex items-start justify-between mb-4">
                    <div className={`w-16 h-16 rounded-3xl bg-gradient-to-br ${category.gradient} flex items-center justify-center shadow-lg group-hover:scale-110 transition-transform duration-300`}>
                      <span className="text-2xl">{category.emoji || '‚ú®'}</span>
                    </div>
                    <div className="text-right">
                      <div className="text-lg font-bold text-slate-700">{category.count ?? 0}</div>
                      <div className="text-xs text-slate-400">–∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–∞</div>
                    </div>
                  </div>
                  <h3 className="text-xl font-bold text-slate-800 mb-2 group-hover:text-slate-900 transition-colors">
                    {category.label}
                  </h3>
                  {category.description && (
                    <p className="text-sm text-slate-600 mb-4 leading-relaxed">{category.description}</p>
                  )}
                  <div className="flex items-center justify-between">
                    <Badge variant="secondary" className={`text-xs font-medium px-3 py-1 bg-gradient-to-r ${category.gradient} text-white border-0`}>
                      –†–∞–∑–≥–ª–µ–¥–∞–π –∫–∞—Ç–µ–≥–æ—Ä–∏—è
                    </Badge>
                    <ArrowLeft className="w-4 h-4 text-slate-400 rotate-180 group-hover:translate-x-1 transition-transform duration-300" />
                  </div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}

      {/* Stats */}
      {!loading && !error && categories.length > 0 && (
        <div className="mt-12 text-center">
          <div className="inline-flex items-center gap-6 bg-white/80 border border-slate-200/50 rounded-2xl px-6 py-4 backdrop-blur-sm">
            <div className="text-center">
              <div className="text-2xl font-bold text-slate-800">{filtered.length}</div>
              <div className="text-xs text-slate-500">–ö–∞—Ç–µ–≥–æ—Ä–∏–∏</div>
            </div>
            <div className="w-px h-8 bg-slate-300" />
            <div className="text-center">
              <div className="text-2xl font-bold text-slate-800">{totalTools}</div>
              <div className="text-xs text-slate-500">–û–±—â–æ –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç–∏</div>
            </div>
          </div>
        </div>
      )}
    </main>
  );
}
