import { useEffect, useMemo, useState } from "react";
import { Link } from "react-router-dom";
import { Search, ArrowLeft } from "lucide-react";
import { Card, CardContent } from "../components/ui/card";
import { Badge } from "../components/ui/badge";
import { Input } from "../components/ui/input";

type RawCategory =
  | string
  | {
      id?: string;
      cap?: string; // понякога може да дойде като cap
      label?: string;
      title?: string;
      name?: string;
      count?: number;
      tools_count?: number;
      total?: number;
      emoji?: string;
      gradient?: string;
    };

type Category = {
  id: string;      // cap с префикс, напр. "cap:text-explain"
  label: string;   // четливо име
  count: number;   // брой инструменти
  emoji?: string;  // по желание
  gradient?: string; // за стил (не е задължително)
};

const API_BASE =
  (import.meta as any).env?.VITE_API_BASE ||
  (window as any).__API_BASE__ ||
  "http://127.0.0.1:8000";

// проста функция за правене на четливо име от cap
function prettify(id: string) {
  const noCap = id.replace(/^cap:/, "");
  return noCap
    .split(/[-_]/g)
    .map((s) => (s ? s[0].toUpperCase() + s.slice(1) : s))
    .join(" ");
}

export default function Categories() {
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(true);
  const [cats, setCats] = useState<Category[]>([]);

  useEffect(() => {
    let cancelled = false;
    (async () => {
      try {
        setLoading(true);
        const r = await fetch(`${API_BASE}/catalog/categories`);
        const data: RawCategory[] = await r.json();

        const normalized: Category[] = (data || []).map((c: RawCategory) => {
          if (typeof c === "string") {
            return { id: c, label: prettify(c), count: 0 };
          }
          const id = c.id || c.cap || "";
          const label = c.label || c.title || c.name || prettify(id);
          const count = c.count ?? c.tools_count ?? c.total ?? 0;
          return {
            id,
            label,
            count,
            emoji: (c as any).emoji,
            gradient: (c as any).gradient,
          };
        });

        if (!cancelled) {
          setCats(normalized);
          setLoading(false);
        }
      } catch (e) {
        console.error(e);
        if (!cancelled) {
          setCats([]);
          setLoading(false);
        }
      }
    })();
    return () => {
      cancelled = true;
    };
  }, []);

  const filtered = useMemo(() => {
    const term = search.trim().toLowerCase();
    if (!term) return cats;
    return cats.filter(
      (c) =>
        c.label.toLowerCase().includes(term) ||
        c.id.toLowerCase().includes(term.replace(/\s+/g, "-"))
    );
  }, [cats, search]);

  return (
    <main className="max-w-6xl mx-auto px-4 py-8 pb-24 md:pb-12">
      {/* Заглавие / търсене */}
      <div className="flex items-center justify-between mb-8">
        <div className="flex items-center gap-3">
          <Link
            to="/"
            className="inline-flex items-center gap-2 text-slate-600 hover:text-blue-600 transition-colors p-2 -ml-2 rounded-xl"
          >
            <ArrowLeft className="w-4 h-4" />
            <span className="font-medium">Назад</span>
          </Link>
          <div className="w-px h-6 bg-slate-300" />
          <div>
            <h1 className="text-3xl text-slate-800 font-bold">
              AI Категории Инструменти
            </h1>
            <p className="text-slate-600 text-sm mt-1">
              Данните идват от бекенда /catalog/categories
            </p>
          </div>
        </div>
      </div>

      <div className="mb-8">
        <div className="relative max-w-md">
          <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400 w-5 h-5" />
          <Input
            placeholder="Търсене в категории..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            className="pl-12 bg-white/80 border-slate-200/50 h-12 rounded-2xl backdrop-blur-sm font-medium"
          />
        </div>
      </div>

      {/* Грид */}
      {loading ? (
        <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {Array.from({ length: 9 }).map((_, i) => (
            <div key={i} className="h-40 bg-slate-100 rounded-3xl animate-pulse" />
          ))}
        </div>
      ) : filtered.length === 0 ? (
        <div className="text-sm text-slate-500">Няма категории.</div>
      ) : (
        <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {filtered.map((category) => (
            <Link
              key={category.id}
              to={`/categories/${encodeURIComponent(category.id)}`}
              className="group"
            >
              <Card className="border-slate-200/50 hover:shadow-2xl transition-all duration-300 hover:-translate-y-2 bg-white/90 backdrop-blur-sm overflow-hidden">
                <CardContent className="p-6 relative">
                  <div
                    className={`absolute inset-0 bg-gradient-to-br ${
                      category.gradient || "from-blue-500 to-indigo-500"
                    } opacity-5 group-hover:opacity-10 transition-opacity duration-300`}
                  />
                  <div className="relative">
                    <div className="flex items-start justify-between mb-4">
                      <div
                        className={`w-16 h-16 rounded-3xl bg-gradient-to-br ${
                          category.gradient || "from-blue-500 to-indigo-500"
                        } flex items-center justify-center shadow-lg group-hover:scale-110 transition-transform duration-300`}
                      >
                        <span className="text-2xl">
                          {category.emoji || "✨"}
                        </span>
                      </div>
                      <div className="text-right">
                        <div className="text-xs text-slate-500">
                          {category.count}
                        </div>
                        <div className="text-xs text-slate-400">
                          инструмента в тази категория
                        </div>
                      </div>
                    </div>

                    <h3 className="text-xl font-bold text-slate-800 mb-2 group-hover:text-slate-900 transition-colors">
                      {category.label}
                    </h3>

                    <div className="flex items-center justify-between">
                      <Badge
                        variant="secondary"
                        className={`text-xs font-medium px-3 py-1 bg-gradient-to-r ${
                          category.gradient || "from-blue-500 to-indigo-500"
                        } text-white border-0`}
                      >
                        Разгледай категория
                      </Badge>
                      <span className="text-slate-400">→</span>
                    </div>
                  </div>
                </CardContent>
              </Card>
            </Link>
          ))}
        </div>
      )}
    </main>
  );
}
