// frontend/src/App.tsx
import React, { useState } from 'react';
import {
  Search, Star, MessageCircle, Zap, Sparkles
} from 'lucide-react';

import { Button } from './components/ui/button';
import { Input } from './components/ui/input';
import { Badge } from './components/ui/badge';
import { Card, CardContent } from './components/ui/card';

/* ---------- Types matching backend ---------- */
type Tool = { name: string; link?: string | null };
type Group = { title: string; tools: Tool[] };
type PlanItem = { task: string; tools: Tool[] };
type PlanResponse = { goal: string; plan: PlanItem[]; groups?: Group[] };

/* fallback: групиране на plan -> groups, ако бекендът (по някаква причина) не върне groups */
function groupsFromPlanFallback(plan: PlanItem[]): Group[] {
  const byTitle = new Map<string, Map<string, Tool>>();
  for (const p of plan) {
    const title = p.task;
    const bucket = byTitle.get(title) ?? new Map<string, Tool>();
    for (const t of p.tools) bucket.set(t.name, t);
    byTitle.set(title, bucket);
  }
  return Array.from(byTitle.entries()).map(([title, bucket]) => ({
    title,
    tools: Array.from(bucket.values())
  }));
}

export default function App() {
  const [searchQuery, setSearchQuery] = useState('');
  const [chatInput, setChatInput] = useState('');
  const [groups, setGroups] = useState<Group[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  async function fetchPlan(prompt: string): Promise<PlanResponse> {
    const res = await fetch('http://127.0.0.1:8000/plan', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ user_goal: prompt }),
    });
    if (!res.ok) throw new Error('Failed to fetch tools from backend');
    return res.json();
  }

  const handleFindTools = async () => {
    setLoading(true);
    setError(null);
    try {
      const data = await fetchPlan(chatInput);
      const grouped =
        data.groups && data.groups.length ? data.groups : groupsFromPlanFallback(data.plan);
      setGroups(grouped);
    } catch (e) {
      console.error(e);
      setError('⚠️ Something went wrong while fetching tools.');
    } finally {
      setLoading(false);
    }
  };

  // филтриране по търсене (по заглавие на група или име на инструмент)
  const term = searchQuery.trim().toLowerCase();
  const filteredGroups: Group[] = groups
    .map(g => ({
      title: g.title,
      tools: g.tools.filter(t =>
        !term ||
        g.title.toLowerCase().includes(term) ||
        (t.name ?? '').toLowerCase().includes(term)
      ),
    }))
    .filter(g => g.tools.length > 0);

  // брой уникални инструменти (за брояча горе)
  const uniqueToolCount = new Set(
    filteredGroups.flatMap(g => g.tools.map(t => t.name))
  ).size;

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-50">
      {/* Header */}
      <header className="sticky top-0 z-50 bg-white/70 backdrop-blur-xl border-b border-slate-200/50">
        <div className="max-w-6xl mx-auto px-4 py-4 flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <div className="w-9 h-9 bg-gradient-to-br from-blue-500 to-indigo-600 rounded-xl flex items-center justify-center shadow-lg shadow-blue-500/25">
              <Sparkles className="w-5 h-5 text-white" />
            </div>
            <span className="text-xl text-slate-800 font-semibold">My AI</span>
          </div>
          <nav className="hidden md:flex items-center space-x-8">
            <a href="#" className="text-slate-600 hover:text-blue-600 font-medium">Discover</a>
            <a href="#" className="text-slate-600 hover:text-blue-600 font-medium">Categories</a>
            <a href="#" className="text-slate-600 hover:text-blue-600 font-medium">Learn</a>
            <Button variant="outline" size="sm" className="border-blue-300 text-blue-600 hover:bg-blue-50 font-medium">
              Sign In
            </Button>
          </nav>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-6xl mx-auto px-4 py-12 pb-24 md:pb-12">
        {/* Hero */}
        <section className="text-center mb-16">
          <h1 className="text-5xl md:text-6xl text-slate-800 mb-6 font-bold tracking-tight">
            Discover AI tools that <span className="bg-gradient-to-r from-blue-600 to-indigo-600 bg-clip-text text-transparent">actually help</span>
          </h1>
          <p className="text-xl text-slate-600 mb-10 max-w-2xl mx-auto leading-relaxed">
            From writing essays to creating art, find the perfect AI tools for your goals.
          </p>

          {/* Input */}
          <div className="max-w-2xl mx-auto mb-10">
            <div className="relative bg-white/80 backdrop-blur-sm rounded-3xl p-8 border border-slate-200/50 shadow-2xl shadow-blue-500/10">
              <div className="flex items-center space-x-4 mb-6">
                <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-indigo-600 rounded-2xl flex items-center justify-center shadow-lg shadow-blue-500/25">
                  <MessageCircle className="w-6 h-6 text-white" />
                </div>
                <span className="text-slate-700 text-lg font-medium">What do you want to achieve with AI?</span>
              </div>
              <div className="flex space-x-3">
                <Input
                  placeholder="e.g., Write better emails, create a logo, analyze data..."
                  value={chatInput}
                  onChange={(e) => setChatInput(e.target.value)}
                  className="flex-1 bg-slate-50/80 border-slate-200 text-base h-14 rounded-2xl px-5 font-medium placeholder:text-slate-400"
                />
                <Button onClick={handleFindTools} className="bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 px-8 h-14 rounded-2xl shadow-lg shadow-blue-500/25 font-medium">
                  <Zap className="w-5 h-5 mr-2" />
                  Find Tools
                </Button>
              </div>
            </div>
          </div>

          {/* Error or Loading Messages */}
          {error && <div className="text-red-600 font-medium mb-6">{error}</div>}
          {loading && <div className="text-blue-600 font-medium mb-6">🔄 Finding the best tools for you...</div>}
        </section>

        {/* Filters (засега само search е активен) */}
        <section className="mb-10">
          <div className="flex flex-col sm:flex-row gap-4 mb-8">
            <div className="relative flex-1">
              <Search className="absolute left-4 top-1/2 transform -translate-y-1/2 text-slate-400 w-5 h-5" />
              <Input
                placeholder="Search tools, categories, or goals..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-12 bg-white/80 border-slate-200/50 h-12 rounded-2xl backdrop-blur-sm font-medium"
              />
            </div>
          </div>
        </section>

        {/* Results (GROUPED) */}
        <section>
          <div className="flex items-center justify-between mb-8">
            <h2 className="text-3xl text-slate-800 font-bold">
              {searchQuery ? `Results for "${searchQuery}"` : 'Generated AI Tools'}
            </h2>
            <span className="text-slate-500 font-medium">{uniqueToolCount} tools</span>
          </div>

          {filteredGroups.length === 0 && !loading && (
            <div className="text-center py-16">
              <div className="w-20 h-20 bg-slate-100 rounded-3xl flex items-center justify-center mx-auto mb-6">
                <Search className="w-10 h-10 text-slate-400" />
              </div>
              <h3 className="text-slate-800 mb-3 font-bold text-xl">No tools found</h3>
              <p className="text-slate-600">Try adjusting your search.</p>
            </div>
          )}

          {/* секции по групи */}
          <div className="space-y-12">
            {filteredGroups.map(group => (
              <section key={group.title} className="space-y-5">
                {/* Заглавие на стъпката */}
                <h3 className="text-xl font-semibold text-slate-800">{group.title}</h3>

                {/* Картите за инструментите */}
                <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6">
                  {group.tools.map((tool) => {
                    const IconComponent = Sparkles;
                    return (
                      <Card key={`${group.title}-${tool.name}`} className="border-slate-200/50 hover:shadow-xl transition-all duration-300 hover:-translate-y-2 bg-white/80 backdrop-blur-sm group">
                        <CardContent className="p-6">
                          <div className="flex items-start justify-between mb-5">
                            <div className="w-14 h-14 bg-gradient-to-br from-blue-400 to-indigo-500 rounded-2xl flex items-center justify-center shadow-lg group-hover:scale-110 transition-transform duration-300">
                              <IconComponent className="w-7 h-7 text-white" />
                            </div>
                            <div className="flex items-center space-x-1 bg-yellow-50 px-3 py-1.5 rounded-xl">
                              <Star className="w-4 h-4 text-yellow-500 fill-current" />
                              <span className="text-sm text-yellow-700 font-medium">4.5</span>
                            </div>
                          </div>
                          <h3 className="text-slate-800 mb-2 font-bold text-lg">{tool.name}</h3>
                          <p className="text-slate-600 text-sm mb-5 leading-relaxed">{/* оставяме празно/по избор */}</p>
                          <div className="flex space-x-2 mb-5">
                            <Badge variant="secondary" className="text-xs bg-blue-100 text-blue-700 font-medium px-3 py-1">
                              Generated
                            </Badge>
                            <Badge variant="secondary" className="text-xs font-medium px-3 py-1 bg-emerald-100 text-emerald-700">
                              Free
                            </Badge>
                          </div>

                          {tool.link ? (
                            <a
                              href={tool.link}
                              target="_blank"
                              rel="noreferrer"
                              className="block w-full text-center bg-gradient-to-r from-red-500 to-pink-500 hover:from-red-600 hover:to-pink-600 text-white rounded-2xl h-11 leading-[44px] font-medium shadow-lg shadow-red-500/25"
                            >
                              Open Tool
                            </a>
                          ) : (
                            <Button disabled className="w-full rounded-2xl h-11">
                              No Link
                            </Button>
                          )}
                        </CardContent>
                      </Card>
                    );
                  })}
                </div>
              </section>
            ))}
          </div>
        </section>
      </main>
    </div>
  );
}
