import { useEffect, useMemo, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { Card, CardContent } from "../components/ui/card";
import { Badge } from "../components/ui/badge";
import { Button } from "../components/ui/button";
import { Skeleton } from "../components/ui/skeleton";
import { Separator } from "../components/ui/separator";
import { ToggleGroup, ToggleGroupItem } from "../components/ui/toggle-group";
import { ArrowLeft } from "lucide-react";

type Tool = {
  id?: number | string;
  name: string;
  description?: string;
  tags?: string[];
  icon?: string | null;
  link?: string | null;
};

const API_BASE = process.env.REACT_APP_API_BASE || "http://127.0.0.1:8000";

// Metadata for categories
const FALLBACK: Record<string, { label: string; emoji: string; hint: string }> = {
  "cap:text-explain": { label: "Text & Writing", emoji: "‚úçÔ∏è", hint: "chat, writing, editing" },
  "cap:text-summarize": { label: "Summarizers", emoji: "üìù", hint: "TL;DR, notes" },
  "cap:image-generate": { label: "Images & Design", emoji: "üé®", hint: "generators, editors" },
  "cap:video-generate": { label: "Video", emoji: "üé¨", hint: "make & edit videos" },
  "cap:audio-transcribe": { label: "Audio & Speech", emoji: "üéôÔ∏è", hint: "transcribe, voice" },
  "cap:research-web": { label: "Education & Research", emoji: "üîé", hint: "search, sources" },
  "cap:slide-generate": { label: "Slides & Docs", emoji: "üìä", hint: "decks, docs" },
  "cap:automate-workflow": { label: "Automation", emoji: "ü§ñ", hint: "connect apps & flows" },
  "cap:integrations": { label: "Integrations", emoji: "üîå", hint: "APIs, webhooks" },
  "cap:doc-read-pdf": { label: "PDF & Docs", emoji: "üìÑ", hint: "read & analyze PDFs" },
};

export default function CategoryView() {
  const { cap = "" } = useParams();
  const [loading, setLoading] = useState(true);
  const [tools, setTools] = useState<Tool[]>([]);
  const [filter, setFilter] = useState<string | null>(null);

  useEffect(() => {
    let cancelled = false;
    setLoading(true);
    (async () => {
      try {
        const r = await fetch(`${API_BASE}/catalog/categories/${encodeURIComponent(cap)}`);
        const data = await r.json();
        const arr: Tool[] = data?.tools || data || [];
        if (!cancelled) {
          setTools(arr.map((t: any) => ({
            id: t.id ?? t.name,
            name: t.name,
            description: t.description ?? "",
            tags: t.tags ?? [],
            icon: t.icon ?? t.icon_url ?? null,
            link: t.link ?? t.links?.website ?? null,
          })));
          setLoading(false);
        }
      } catch {
        if (!cancelled) {
          setTools([]);
          setLoading(false);
        }
      }
    })();
    return () => { cancelled = true; };
  }, [cap]);

  const shown = useMemo(() => {
    if (!filter) return tools;
    const f = filter.toLowerCase();
    return tools.filter((t) => (t.tags || []).some((x) => (x || "").toLowerCase().includes(f)));
  }, [tools, filter]);

  const meta = FALLBACK[cap] || { label: cap, emoji: "üß∞", hint: "AI tools" };

  return (
    <main className="mx-auto max-w-7xl px-4 py-8">
      <div className="flex items-end justify-between gap-4 mb-4">
        <div>
          <div className="flex items-center gap-2 text-3xl font-bold tracking-tight">
            <span>{meta.emoji}</span>
            <h1 className="break-words">{meta.label}</h1>
          </div>
          <p className="text-muted-foreground text-sm mt-1">{meta.hint}</p>
        </div>

        <ToggleGroup type="single" value={filter ?? ""} onValueChange={(v) => setFilter(v || null)} className="bg-muted rounded-xl p-1">
          <ToggleGroupItem value="" aria-label="All">All</ToggleGroupItem>
          <ToggleGroupItem value="free" aria-label="Free">Free</ToggleGroupItem>
          <ToggleGroupItem value="trending" aria-label="Trending">Trending</ToggleGroupItem>
          <ToggleGroupItem value="new" aria-label="New">New</ToggleGroupItem>
        </ToggleGroup>
      </div>

      <Separator className="my-6" />

      {loading ? (
        <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-4">
          {Array.from({ length: 9 }).map((_, i) => (
            <Skeleton key={i} className="h-40 rounded-2xl" />
          ))}
        </div>
      ) : shown.length === 0 ? (
        <div className="text-sm text-muted-foreground">No tools found in this category.</div>
      ) : (
        <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-4">
          {shown.map((t) => (
            <a key={t.id} href={t.link || "#"} target="_blank" rel="noreferrer" className="group">
              <Card className="rounded-2xl h-full hover:shadow-md transition">
                <CardContent className="pt-5">
                  <div className="flex items-center gap-3 mb-2">
                    {t.icon ? (
                      <img src={t.icon} alt="" className="h-6 w-6 rounded" />
                    ) : (
                      <span className="text-lg">üß†</span>
                    )}
                    <span className="truncate font-semibold">{t.name}</span>
                  </div>
                  <p className="text-sm text-muted-foreground line-clamp-3">{t.description || "‚Äî"}</p>
                  <div className="mt-3 flex flex-wrap gap-2">
                    {(t.tags || []).slice(0, 4).map((x, i) => (
                      <Badge key={i} variant="secondary" className="capitalize">{x}</Badge>
                    ))}
                  </div>
                  <div className="mt-4">
                    <Button size="sm" variant="outline">Open</Button>
                  </div>
                </CardContent>
              </Card>
            </a>
          ))}
        </div>
      )}

      <div className="mt-10">
        <Link to="/categories">
          <Button variant="ghost" className="inline-flex items-center gap-2">
            <ArrowLeft className="w-4 h-4" />
            Back to Categories
          </Button>
        </Link>
      </div>
    </main>
  );
}
